css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("thenextweb.com") {
  /* Hide 'canvas' adverts */
  #canvas {
    display: none;
  }
  
  /* Hide header and 'announcements' */
  #announcement {
    display: none;
  }
  #top {
    display: none !important;
  }
  .l-postSingle.l-postSingle--interstitial {
    margin-top: 0px;
  }
  .l-postSingle-content-main-share {
    display: none;
  }
  .js-scrollHelper {
    display: none;
  }
  
  body {
    padding-top: 0px;
  }

  /* Don't slide or bounce article */
  .l-postSingle--bounce .l-postSingle-wrap {
    animation: none;
  }
  div.l-postSingle {
    transform:none !important;
  }
  body.display-interstitial {
    overflow: auto;
  }
  
  /* Hide suggested articles */
  .l-postSingle-content .l-postSingle-content-sidebar {
    display: none;
  }
  .sidebar-block {
    display: none;
  }
  .l-postSingle-content .l-postSingle-content-main {
    width: 100%;
  }
  .l-postSingle-content-main {
    width: 100%;
  }
  .relatedPosts {
    display: none;
  }
}
