css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
url-prefix("http://al-s096:8080/tfs")
{

.withTitle {
    color: #666;
}
.tbTile .witAssignedTo, .tbPivotItem .witTitle, .toolbar {
    color: #888;
}
.ui-tabs .ui-tabs-nav li a, .sprint-capacity-summary-part div, input, textarea, select,
.ui-state-disabled, .ui-widget-content .ui-state-disabled, .ui-widget-header .ui-state-disabled, .grid-row, .tag-box, .tag-delete, .tree-drag-tile.folder > .node-link > .node-content, .tree-view .folder > .node-link > .node-content {
    color: #bbb !important;
}
.tbTile .witTitle, .hub-title, body, input, textarea, .ui-widget-content, .ui-tabs .ui-tabs-nav li.ui-tabs-selected a, .ui-tabs .ui-tabs-nav li a:hover, .tags-add-button .tag-box-selectable, button, button.ui-state-default, button.ui-button, .hub-title, .ui-dialog .ui-dialog-title, .boards-controls-limit .limit {
    color: #ddd !important;
}
.witRemainingWork, .pivot-filter a, .witExtra .hover {
    color: #dedede !important;
}
.header-post11 .hubs li.selected a, .header-post11 .hub-groups-section li.selected a, .header-post11 .hub-groups-section li a:hover, .header-post11 .hubs li a:hover, .pivot-view a:hover, .pivot-view li.selected a, .ui-widget-content a {
    color: #3c57cb !important;
}
a, a:hover, a:visited {
    color: #3c57cb;
}
.boards-controls-limit.under .current {
    color: #7bd996;
}
.hub-pivot-content, body, input, textarea, button, button.ui-state-default, button.ui-button, input, textarea, select, .combo-drop-popup, .witRemainingWork input {
    background-color: #191919 !important;
}
.content-section, .ui-dialog, button:hover, button:focus, .menu.sub-menu > .menu-item.hover, .vc-diff-viewer .ln-o, .vc-diff-viewer .ln-m {
    background-color: #222 !important;
}
.tbTile .tbTileContent.tile-dimmed, .header-post11 #navWrapper, .dragHover, .ui-tabs .ui-tabs-nav, .ui-dialog-buttonpane, 
.ui-state-disabled, .ui-widget-content .ui-state-disabled, .ui-widget-header .ui-state-disabled, .richeditor-toolbar, .grid-row-selected-blur, .grid-row:hover, .grid-row:active,
.grid-row-current.grid-row-selected, .grid-gutter-row-normal, .menu, .add-panel,
.tree-view > ul.tree-children li.selected > a > div.node-content, .tree-view.focus > ul.tree-children li.selected > a > div.node-content, li.tree-drag-tile > a > div.node-content, .grid-row:hover, .tree-view > ul.tree-children li.focus > a > div.node-content, .tree-view > ul.tree-children li.hover > a > div.node-content, .tree-view > ul.tree-children li.context-menu-active > a > div.node-content, .vc-history-list .results-container .group-header, .splitter.toggle-button-enabled.collapsed > .handleBar, .sidebar-search .search-input-wrapper, .testmanagement-plans-combo, .ui-dialog .ui-dialog-titlebar, .agile-board .board-tile, .board-tile.ui-draggable-dragging, .vc-change-summary .collapsible-section .tfs-collapsible-header, .combo-drop-popup li:hover, .combo-drop-popup,  li.selected, .grid-header-column:hover {
    background-color: #333 !important;
}
.combo, .splitter > .handleBar, .splitter > .handleBar-clone, .combo.invalid, .tile-dimmed .witExtra .hover {
    background-color: #555;
}
.tbTile .tbTileContent, .header-post11 #header-row, .tags-add-button .tag-box-selectable, .tag-box, .tag-delete, .toolbar .menu-bar .menu-item.hover, .workitemcontrol .menu-bar .menu-item.hover, .inprogress .board-tile {
    background-color: #2d3868 !important;
}
.witExtra .hover{
    background-color: #3c57cb;
}    
.vc-diff-viewer .deleted-content {
    background-color: #3a1c1c;
}
.vc-diff-viewer .added-content {
    background-color: #243826;
}
.grid-gutter-row-current, .hub-splitter.splitter.horizontal > .leftPane, .taskboard { 
    border-color: #191919 !important;
}
.combo, .richeditor-container, .richeditor-toolbar, .menu, .menu.sub-menu > .menu-item.hover, .add-panel,
    .tree-view > ul.tree-children li.selected > a > div.node-content, .tree-view.focus > ul.tree-children li.selected > a > div.node-content, li.tree-drag-tile > a > div.node-content, .grid-row-current.grid-row-selected, .grid-row:hover, .grid-row:active,
    .grid-row-selected-blur, .grid-row:hover, .tree-view > ul.tree-children li.focus > a > div.node-content, .tree-view > ul.tree-children li.hover > a > div.node-content, .tree-view > ul.tree-children li.context-menu-active > a > div.node-content, .sidebar-search .search-input-wrapper, .testmanagement-plans-combo, .agile-board .board-tile, .board-tile.ui-draggable-dragging, .vc-diff-viewer .ellipsis-line.middle .ellipsis-line-content, .combo-drop-popup, .combo-drop-popup li:hover, .combo-drop-popup li.selected {
    border-color: #555 !important;
}
.grid-gutter-row-normal {
    border-color: #333;
}
.taskboard-cell {
    border-color: #aaa;
}
.tbTile .tbTileContent, .header-post11 .hubs li.selected a, .inprogress .board-tile {
    border-color: #3c57cb !important;
}
.tbTile .tbTileContent.tile-dimmed, .ui-widget-content, 
.ui-state-disabled, .ui-widget-content .ui-state-disabled, .ui-widget-header .ui-state-disabled, 
button, button.ui-state-default, button.ui-button, .tag-container, .tag-box, .tag-delete, .vc-diff-viewer .ln-o, .vc-diff-viewer .ln-m, .vc-change-summary-files .item-details .vc-diff-viewer .text-diff-container {
    border-color: #666 !important;
}
  
.backlog .grid-cell {
    transition: text-shadow 0.25s;
}
.backlog .grid-cell:hover {
    text-shadow: 0 0 4px #bbb;
}
}
