css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.20min.ch") {

#site-wrapper { 
  background-color: #191919 !important; 
} 
textarea, input { 
  background-color: #222 !important; 
} 
html.backgroundfill, .add_comment, .rate_button::after, .info_panel, .replies li.comment, .topelement, #adapticker .toolbar { 
  background-color: #333 !important; 
}
body, .info_panel, h1, .topelement, .teaser_title h2 a, .box.x30 .teaser a:hover { 
  color: #bbb !important; 
} 
h3, .teaser a:hover, .story_content .story_links ul li h5 span, .box.x30 .teaser a, .teaser_thematiclinks ul li a:hover, .sprint-capacity-summary-part div, .directory_col ul li a, .directory_col ul li a:hover, .story_infobox .info_panel_spread .entry a, .story_infobox .info_panel_spread .entry a:hover, .topelement .caption .restart_link:link, .topelement .caption .restart_link:visited, .story_content .story_links ul li h5 a:hover, #adapticker { 
  color: #aaa; 
} 
.teaser a, #header .textlink a, .story .story_head h3, .story .story_head .story_media p, .story_content .story_links ul li h5 a, #veeseoRA2VW20minH1.veeseoRA2VW20minH1 h3, #veeseoRA2VW20minH1.veeseoRA2VW20minH1 .recommendation h5, .teaser_thematiclinks ul li a, #footer-center .left, #header .textlink a:hover, .teaser_media ul li a:hover {
  color: #999;    
}
#talkback .author, .add_comment textarea, .add_comment input, #talkback .author:hover, .add_comment textarea:hover, .add_comment input:hover, a, a:hover, .story_content a, .story_content a:hover, #adapticker a, #adapticker a:hover { 
  color: #007acc; 
}
textarea, input, .topelement .gallerybar, #ad_js_deal_container_wideboard, #ad_js_deal_container_double { 
  border-color: #999 !important; 
}
#footer { 
  background: #222; 
}
.box .teaser.panelbox { 
  background: #333; box-shadow: 2px 2px 3px 0 rgba(225, 225, 225, 0.35) 
}
.box .teaser.panelbox, .story_content .story_links, .gradient_light, #veeseoRA2VW20minH1.veeseoRA2VW20minH1 .recommendation { 
  background: linear-gradient(to bottom, #333 43%, #191919 100%) !important; 
}
.box.x30 .teaser.promotion, #veeseoRA2VW20minH1.veeseoRA2VW20minH1 .recommendation li.zanox, .section-divider {
  background-color: #2d3868;
}
  
  
  
.topelement, .topelement .back, .topelement .forward
  {
    background-image: none;
  }
.topelement .back{
  border-right: 2px solid #aaa;
}
.topelement .forward{
  border-left: 2px solid #aaa;
}
.teaser_title h2 a, .story_content a, a, .directory_col ul li a { 
  transition: text-shadow 0.25s; 
}
a:hover {
  text-shadow: 0 0 4px #007acc;
}
.teaser_title h2 a:hover { 
  text-shadow: 0 0 5px #ddd; 
} 
.directory_col ul li a:hover { 
  text-shadow: 0 0 5px #bbb; 
} 
.story_content a:hover {
  text-shadow: 0 0 4px #007acc; 
}
#micronavi ul li a:hover, #header ul#banderole li a:hover, .barrier a:hover {
  text-shadow: 0 0 4px #fff; 
}
.teaser_title h2 a:hover, .box.x30 .teaser a:hover { 
  text-shadow: 0 0 4px #bbb; 
} 
.teaser a:hover, .box.x30 .teaser a:hover, .teaser_thematiclinks ul li a:hover, .directory_col ul li a:hover, .directory_col ul li a:hover, .story_infobox .info_panel_spread .entry a:hover, .story_content .story_links ul li h5 a:hover, .recommendation a:hover { 
  text-shadow: 0 0 4px #aaa; 
} 
.teaser a:hover, #header .textlink a:hover, .story_content .story_links ul li h5 a:hover, .teaser_thematiclinks ul li a:hover {
  text-shadow: 0 0 4px #999;    
}
.teaser.panelbox.lateststories a:hover {
  text-shadow: 0 0 4px #007acc; 
}
}
