css:
@-moz-document domain("twitter.com") {   

body {
background-color: #1b1b1b !important;
}      
    
/* header */    
.global-nav-inner, .global-nav, .global-nav .container {
    border-bottom: 0px solid rgba(0, 0, 0, 0.15);
    background-color: transparent !important;         
    height: 46px;
    position: relative;
    width: 100%;
}
 
.js-global-new-tweet.js-tooltip.btn.primary-btn.tweet-btn, .Icon.Icon--cog.Icon--large, .global-dm-nav.js-tooltip, .Icon.Icon--me.Icon--large, .Icon.Icon--notifications.Icon--large, .Icon--home:before {
    color: #4C475E;
}        
    
.top-timeline-tweetbox .timeline-tweet-box, .home-tweet-box, .top-timeline-tweetbox .tweet-user, .expand-action-wrapper, .Icon.Icon--discover.Icon--large, .text, .caret-inner, .search-icon.js-search-action, .search-input, .Icon.Icon--bird.bird-topbar-etched    {
        display: none !important;
        border-color: #1b1b1b !important;
    }
    
/* sidebar/trends/copyright/who to follow*/        
     
.DashboardProfileCard-screennameLink {
        display: none !important;
    }
    
.ProfileSidebar .Footer-item, .Footer-link, .ProfileSidebar .Footer:hover .Footer-link {
    color: #6c7393 !important;
    display: none
}    

.ProfileSidebar .Trends {
    background-color: #1b1b1b;
    display: none
}
    
/* action button color */    
    
.ProfileTweet-actionButton, .ProfileTweet-actionButtonUndo {
    color: #4C475E;
}    
   
/* Trends colors */    
.module .flex-module:last-child, .module .flex-module, .flex-module, .trend-location {
    background-color: #1b1b1b !important;
    border: none !important;
    border-radius: 0px;
    color: #4C475E !important;
    display: none
}  

.change-trends, .trends-container > div:nth-child(1) > small:nth-child(2) {
    color: #1b1b1b !important;
    display: none
}    
.change-trends:hover {
    color: #aaa !important;
    display: none
}    
    
/* 'tweets' header bar */        
        
.header-inner, .js-timeline-title  {
        display: none !important;
    }
    
.conversation-module .conversation-header, .conversation-module .conversation-tweet-item, .conversation-module .missing-tweets-bar, .conversation-module .original-tweet-item, .conversation-module .conversation-header .content, .conversation-module .missing-tweets-bar .content {
    background-color: #1b1b1b !important;
}    
    
.vellip, .vellip:after, .vellip:before {
    display: none;
}    
    
/* sidebar */      
    
.DashboardProfileCard-statLabel {
    color: #4C475E;
    font-size: 12px;
    letter-spacing: 0.02em;
    text-transform: none;
    transition: color 0.15s ease-in-out 0s;
    overflow: hidden;
}    
    
.DashboardProfileCard-statValue    {
    color: #4C475E;
    font-size: 12px;
    letter-spacing: 0.02em;
    text-transform: none;
    transition: color 0.15s ease-in-out 0s;
    overflow: hidden;
}   
    
.DashboardProfileCard-avatarLink.u-inlineBlock, .dropdown-divider, .DashboardProfileCard-bg, .module .tweet-box {
           display: none !important;
    }
    
.DashboardProfileCard-name, .DashboardProfileCard-userFields {
    display: none;
}    
    
.DashboardProfileCard-content {
        padding: 10px 20px 10px 40px;
      /*  border-radius: 4px; */
        border-color: #1b1b1b !important;
        background-color: #1b1b1b !important;
        color: #aaa !important;
        border: none;
    }
    
/* button color */
    
  .btn, .btn:hover, .btn.small, .follow-combo.open .btn-user-actions, .dropdown.open .user-dropdown {
    color: #1b1b1b;
    text-decoration: none;
    background-color: #4C475E !important;
   background-image: linear-gradient(#4C475E, #4C475E) !important;
   border-color: #1b1b1b  !important;
}   
    
.WhoToFollow-title {
    color: #4C475E;
}    
    
/* comment out to remove the sidebar */
    
/*
    div.dashboard:nth-child(1)    
    {
      display: none; 
}      
*/
    
 .topbar {
background-color: #1b1b1b;
}    
    
/* content */               
    
.stream-item.js-new-items-bar-container, .module, .DashboardProfileCard-stats {
    margin-left: 0px;
    padding: 0px 0px;
    border: none !important;
}
    
 .stream-container.conversations-enabled.persistent-inline-actions.light-inline-actions, .content-header, .content-no-header {
    border-width: 0px 0px 0px;
    border-style: solid;
    border-color: #1b1b1b
}    
    
.content-main {
    align-content: center;
    width: 700px;
}    
    
.stream-item, .unfocusable-stream-item {
    background: none repeat scroll 0% 0% #1b1b1b;
    border-left: #1b1b1b;
    border-right: 1px solid #1b1b1b;
}    
 
.WhoToFollow.is-visible, .ProfileNav-item.ProfileNav-item--userActions.u-floatRight.u-textRight.with-rightCaret, .Grid.Grid--withGutter, .dropdown-menu, .tweet:hover, UL.ProfileHeading-toggle {
    background-color: #1b1b1b;
    color: #aaa;
}
.new-tweets-bar.js-new-tweets-bar, .tweet {
    background-color: #1b1b1b;   
    color: #aaa;
    border-bottom: 0px solid #1b1b1b;
    border-top: 1px solid #1b1b1b;
    border-left: 1px solid #1b1b1b;
    border-bottom: 1px solid #1b1b1b;
    }
    
.content-main .expansion-container > .original-tweet-container, .content-main .expansion-container > li .tweet, .content-main .expansion-container > li .inline-reply-tweetbox, .content-main .expansion-container > li + li:last-child .tweet.original-tweet, .content-main .stream-items > .stream-item:last-child .expansion-container > li .tweet, .content-main .stream-items > .stream-item:last-child .expansion-container > li .inline-reply-tweetbox, .content-main .stream-items > .stream-item:last-child .expansion-container > li .view-more-container {
    border-bottom: 0px solid #1b1b1b;
    border-radius: 0px;
}    
    
.conversation-module .conversation-tweet-item .tweet:hover, .content-main .expansion-container .tweet.ancestor, .content-main .expansion-container .tweet.descendant, .content-main .expansion-container .view-more-container, .content-main .expansion-container .inline-reply-tweetbox, .home-tweet-box, .dm-tweetbox, .WebToast-box--altColor, .content-main .conversations-enabled .expansion-container .inline-reply-tweetbox, .permalink-tweet:hover, .permalink-tweet.focus, .stream-item.open .original-tweet:hover, .stream-item.open .original-tweet.focus, .simple-tweet.tweet.js-stream-tweet.js-actionable-tweet.js-profile-popup-actionable.has-cards.ancestor {
    background-color: #1b1b1b;                   
    }    

.WhoToFollow-users .fullname , .dropdown-menu li > a, .dropdown-menu .dropdown-link {
    color: #4C475E;
    }
    
    
/* tweet colors */            
    
.account-group:hover .fullname, .account-group:focus .fullname, .account-summary:focus .fullname {
    text-decoration: none;
}

.ProfileTweet-fullname, .fullname {
    color: #4C475E;
}    
    
.ProfileTweet .ProfileTweet-actionButton, .ProfileTweet .ProfileTweet-actionButtonUndo,  .pretty-link:hover s, .pretty-link:focus s, .stream-item-activity-me .latest-tweet .tweet-row a:hover s, .stream-item-activity-me .latest-tweet .tweet-row a:focus s, .account-group:hover .username, .account-group:focus .username, .account-summary:focus .username {
    color: #4C475E;
}
    
.ProfileTweet-screenname.u-inlineBlock.u-dir, .username s, .account-group-inner, .username, .time, .time a, .metadata, .metadata button.btn-link, .metadata a {
    font-size: 13px;
    color: #4C475E;
}    

.with-icn:hover b, .with-icn:focus b {
    text-decoration: none;
    color: #4C475E;
}
.ProfileTweet-actionCount.js-actionCount.js-tooltip .js-short-timestamp, .tweet .context .pretty-link s, .tweet .context .pretty-link b, .js-retweet-text, .expand-stream-item.js-view-details, .with-icn b {
    font-weight: normal;
    color: #4C475E;
}
    
/* profiles */                
    
.ProfileTweet {
    background-color: #1b1b1b;
    border: 1px solid #1b1b1b; 
    color: #1b1b1b;
    }
 
.ProfileTweet-text {
    color: #aaa;        
    }
    
  .ProfileSidebar .SignupCallOut, .ProfileSidebar .ProfileLifelineInfo, .ProfileSidebar .WhoToFollow, .ProfileSidebar .EventFollowAll, .ProfileSidebar .Trends {
    background-color: #1b1b1b;
    border: 1px solid #1b1b1b;
}  
    
.ProfileHeading-content {
     background-color: #1b1b1b;
    border: 1px solid #1b1b1b;
} 
    div.DashboardProfileCard-content {
    font-family: cure;
    font-size: 12px;
}

div.new-tweets-bar.js-new-tweets-bar {
    font-family: cure;
    font-size: 12px;
}

div.stream-item-header {
    font-family: cure;
    font-size: 12px;
}

div.tweet.original-tweet.js-stream-tweet.js-actionable-tweet.js-profile-popup-actionable.js-original-tweet.has-cards {
    font-family: cure;
    font-size: 12px;
}

div.tweet.original-tweet.js-stream-tweet.js-actionable-tweet.js-profile-popup-actionable.js-original-tweet.with-social-proof.opened-tweet {
    font-family: cure;
    font-size: 12px;
}

p.js-tweet-text.tweet-text {
    font-family: cure;
    font-size: 12px;
}

span.js-retweet-text {
    font-family: cure;
    font-size: 12px;
}

strong.fullname.js-action-profile-name.show-popup-with-id {
    font-family: cure;
    font-size: 12px;
}


}
