css:
@-moz-document domain("twitter.com") {
body {
    height: 100% !important;
}

.icon {
    background-image: url(https://dl.dropboxusercontent.com/u/28270774/Stylish/Twitter/twitter_web_sprite_icons.png);
}

.account-summary .account-group, .account-summary .account-action {
    padding-top: 5px !important;
}

.Icon--cogWithCaret {
    padding-right: 5px !important;
}

.dm-dialog .twttr-dialog-footer, .rosetta .dm-tweetbox {
    padding-bottom: 9px !important;
}

.go-to-profile, .dm-dialog .twttr-dialog-footer, #global-tweet-dialog .modal-tweet-form-container {
    border-bottom: 1px solid #000 !important;
    border-radius: 0 0 5px 5px !important;
}

.profile-summary .account-summary, body.user-style-xIGBClutchIx .wrapper-profile .profile-card.profile-header .profile-header-inner, .wrapper-profile .profile-card.profile-header .profile-header-inner, .more-tweet-actions .dropdown .dropdown-menu, .session .dropdown-menu, .global-nav .pull-right .dropdown-menu, .global-nav .secondary-nav .dropdown-menu, .dropdown.open .dropdown-menu, .modal-header {
    border-top: 1px solid #000 !important;
    border-radius: 5px 5px 0 0 !important;
}

* {
    color: #ff005b!important;
    text-shadow: none !important;
    -webkit-box-shadow: none !important;
    -moz-box-shadow: none !important;
    border-color: #000 !important;
}

.flex-module.profile-banner-footer.clearfix {
    border-top: 1px solid #000 !important;
}

.new-tweets-bar.js-new-tweets-bar, .dm-message {
    background: #000 !important;
}

a:link {
    color: #ff005b !important;
}

pre, a:visited {
    color: #ff005b !important;
}

h1, h1 *, h2, h2 *, h3, h3 *, h4, h4 *, h5, h5 *, h6, h6 *, a:hover {
    color: #ff005b !important;
}

b, b *, strong, strong * {
    color: #ff005b !important;
}

li, i, i * {
    color: #ff005b !important;
}

.global-nav {
    -moz-box-shadow: 0 1px 10px 1px #000 !important;
    -webkit-box-shadow: 0 1px 10px 1px #000 !important;
    box-shadow: 0 1px 10px 1px #000 !important;
    background: #000 !important;
}

.stream-item, .unfocusable-stream-item, .stream .stream-item .tweet-actions, .stream .stream-item.open .tweet-actions, .stream .stream-item-activity .stream-item-footer .tweet-actions, .stream .stream-item.open .expanded-conversation .original-tweet-container .tweet-actions {
    background: transparent !important;
}

.modal-overlay, .profile-header-mask, .grid-overlay, .gallery-overlay {
    background: rgba(38,38,38,.65) !important;
}

.ProfileSidebar .ProfileTweetbox, .ProfileSidebar .ProfileSignupCallOut, .ProfileSidebar .ProfileLifelineInfo, .ProfileSidebar .ProfileSocialProof, .ProfileSidebar .ProfilePhotoRail, .ProfileSidebar .WhoToFollow, .ProfileSidebar .Trends, .ProfileSidebar .Footer {
    background-color: #000!important;
    border-color: #000!important;
}

.tweet-btn {
    color: #000!important
}

.dm-dialog .twttr-dialog-header {
    border: 0!important;
    border-bottom: 1px solid #000!important;
    border-left: 1px solid #000!important;
    border-right: 1px solid #000!important;
    border-top: 1px solid #000!important;
}

.DashboardProfileCard-bg {
    border-left: 1px solid #000!important;
    border-top: 1px solid #000!important;
    border-radius: 5px 5px 0 0!important;
    width: 99%!important;
}

.u-borderUserColorLighter, .u-borderUserColorLight, .module .tweet-box, .home-tweet-box, .top-timeline-tweetbox .timeline-tweet-box, .home-tweet-box:not(#foo), .top-timeline-tweetbox .timeline-tweet-box:not(#foo) {
    border-color: #000 !important;
}

.ProfilePage.is-editing .ProfilePage-editingOverlay, .follow-status {
    background-color: #000 !important;
}

.tweet-actions a, .tweet-actions button, .js-refresh-suggestions, .dropdown-menu li>a, .dropdown-menu .dropdown-link, .global-dm-nav, .profile-social-proof, .ProfilePopupContainer .social-proof, .DashboardProfileCard {
    border: 0 !important;
}

.js-mini-profile-stats-container, .module .tweet-box, .profile-summary .account-summary, .account-summary .account-summary-small .js-nav, .more-tweet-actions .dropdown .dropdown-menu, .session .dropdown-menu, .global-nav .pull-right .dropdown-menu, .global-nav .secondary-nav .dropdown-menu, .dropdown.open .dropdown-menu, .rosetta .dm-tweetbox, .ProfileCanopy--large .ProfileCanopy-header , .profile-social-proof, .GalleryTweet, .block-or-report-dialog .submit-section, .dm-tweetbox, div.profile-modal-header.error {
    border-bottom: 1px solid #000 !important;
}

.profile-summary .account-summary, .module .tweet-box, body.user-style-xIGBClutchIx .wrapper-profile .profile-card.profile-header .profile-header-inner, .wrapper-profile .profile-card.profile-header .profile-header-inner, .more-tweet-actions .dropdown .dropdown-menu, .session .dropdown-menu, .global-nav .pull-right .dropdown-menu, .global-nav .secondary-nav .dropdown-menu, .dropdown.open .dropdown-menu, .modal-header, .ProfilePopupContainer .profile-header .profile-header-inner, .ProfilePopupContainer .profile-header .profile-banner-footer, .ProfilePopupContainer .recent-tweets, .go-to-profile, .dm-dialog .twttr-dialog-content, .dm-dialog .twttr-dialog-footer, .rosetta .dm-tweetbox, #global-tweet-dialog .modal-tweet-form-container, .block-or-report-dialog .report-type-section, .block-or-report-dialog .block-section, .profile-modal .tweet, .ProfileHeader, .profile-social-proof, .GalleryTweet, .block-or-report-dialog .submit-section, .dm-tweetbox, div.profile-modal-header.error, .modal-container.tweet-showing .modal-tweet {
    border-left: 1px solid #000 !important;
    border-right: 1px solid #000 !important;
}

.profile-header-inner-overlay, .caret, .inline-reply-caret, .dm-caret, .search-header .btn-group .dropdown-caret, .dropdown-menu .dropdown-caret, .icon.nav-search {
    display: none !important;
}

#global-tweet-dialog .modal-footer, .global-nav-inner, .global-nav .container, .nav li, .content-searchbar, body, .dashboard .stats, .tweet-user, .content-header .header-inner, .content-no-header .no-header-inner, .tweet, .profile-social-proof, .modal-header, #profile_popup .go-to-profile, .dm-thread, .dm-dialog .twttr-dialog-footer, .modal-content, .dm-convo, .dm-tweetbox, .dropdown-menu, .stream-end-inner, .footer, #auto-close p, .module ul:last-child li:last-child .list-link, .module ul:last-child li.last-item .list-link, .module .list-link.last, .module .flex-module:last-child, .module .contrast-flex-module:last-child, .module .list-link, .search-more-options .btn-link, .profile-summary .account-summary, body.user-style-xIGBClutchIx .wrapper-profile .profile-card.profile-header .profile-header-inner, .enhanced-mini-profile .profile-header-inner-overlay, .profile-header-inner .profile-header-inner-overlay, .enhanced-mini-profile .mini-profile .profile-summary, .wrapper-profile .profile-card.profile-header .profile-header-inner, .module .flex-module, .home-tweet-box, .rosetta .dm-tweetbox, .rosetta .WebNotification-buffer--altColor, .content-main .conversations-enabled .expansion-container .inline-reply-tweetbox, .search, .content-main .expansion-container .tweet.ancestor, .content-main .expansion-container .tweet.descendant, .content-main .expansion-container .view-more-container, .content-main .expansion-container .inline-reply-tweetbox, .btn, .content-inner, .content-inner.no-stream-end, .inline-reply-tweetbox, .stream .tweet:hover .tweet-actions, .stream .stream-item.open .expanded-conversation .tweet-actions, #global-tweet-dialog .modal-tweet-form-container, .GalleryTweet, .stream-container .media-thumbnail .grid-tweet-actions-container, .dm-convo-placeholder, .dm-to, .ProfileCanopy-navBar, .ProfileNav-item--userActions, html, .ProfilePage .AppContent, .u-borderUserColorLight, .ProfileTweet, .ProfileCard, .GridTimeline-footerIcon, .mobile-callout .front-welcome, .mobile-callout .front-signup, .mobile-callout .front-signin, .login-responsive .page-canvas, .message, .login-responsive .mobile, .route-resend-password .mobile, .contact-support, .DashboardProfileCard, .follow-card .follow-bar, .u-bgUserColorLightest, .module .tweet-box, .ProfileClusterFollow, .tweet-form .thumbnail-container, .permalink .embed-stats-container, .ProfileHeading-content, .WebToast-box, .block-or-report-dialog .submit-section, .tweet-box.rich-editor.notie, input, button, select, textarea, .empty-timeline-footer, .front-page-photo-set .front-signup, .front-page-photo-set .front-signin  {
    background: #000!important;
}

.message, .DashboardProfileCard.module, .typeahead, .follow-card .follow-bar, #dm_dialog_new, .tweet-box.rich-editor.notie, input, button, select, textarea, .modal-footer {
    border: 1px solid #000 !important;
}

.modal-footer {
    border-radius: 0 0 5px 5px !important;
}
  
.UserActions .user-actions .dropdown-menu, .more-tweet-actions .dropdown .dropdown-menu, .dropdown.open .dropdown-menu {
    border-radius: 5px 5px 5px 5px !important;
}

.GridTimeline-footer:before, .ProfileAvatar, .DashboardProfileCard-avatarLink, .ProfilePopupContainer--bellbird .ProfileHeader .profile-picture {
    background: #000 !important;
}

.front-page-photo-set .front-welcome-text p, .front-page-photo-set .front-welcome-text h1 {
    color: #000 !important;
}
        
.ProfileTweet.is-favorited .ProfileTweet-action--favorite .ProfileTweet-actionButtonUndo *, .tweet.favorited .tweet-actions .Icon--favorite, .tweet.favorited .ProfileTweet-actionList .Icon--favorite {
    color: #00ffff !important;
}

.ProfileTweet-actionButtonUndo * {
    color: #00ffff !important;
}
}
