css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://steamcommunity.com/chat") {

body {
  background-image: url("http://marlamin.com/u/cog.png");
  background-size: cover;
  color: black;
}
  
.chat_dialog_content_inner {
    word-wrap: break-word;
    color: rgb(16,43,65);
}
  
#chatform {
    position: absolute;
    height: 107px;
    left: 5%;
    right: 5%;
    bottom: 0px;
}
  
.friendslist_group_handle {
    height: 20px;
    line-height: 20px;
    background-image: url("https://steamcommunity-a.akamaihd.net/public/images/chat/friendgroup_bg.png");
    background-repeat: repeat-y;
    background-position: left center;
    padding-left: 30px;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    font-size: 11px;
    color: #FFF;
    text-transform: uppercase;
    margin-bottom: 2px;
    cursor: default;
}
  
.chat_page_settings_area {
    position: absolute;
    left: 80px;
    bottom: 3px;
}
  
.maincontent {
    max-width: 100%;
    min-width: 575px;
    width: auto;
}
  
#chat_msg_area {
  border-radius: 66px;
  background-color: rgba(32, 32, 32, 0.81);
}
  
#chat_msg_area {
    position: absolute;
    right: 0px;
    bottom: -20px;
    left: 288px;
    border: 1px solid #000;
    background-color: #383635;
    box-shadow: 1px 1px 0px 0px #373B42;
    top: -100px;
}
  
.chat_dialog_content {
  background: linear-gradient(to bottom, rgba(0, 0, 0, 0.6) 5%, #1C1B19 95%) repeat scroll 0% 0% transparent;
}
  
.chat_page_steam_logo {
  background-image: none;
}
  
.playerAvatar.medium {
  border-radius: 66px;
  left: 10px;
}
  
.playerAvatar.medium img {
  border-radius: 66px;
}
  
.friendslist_entry .playerAvatar {
  border-radius: 66px;
}
  
.friendslist_entry {
    height: 50px;
    padding: 3px;
    margin-bottom: 3px;
    border-top-left-radius: 3px;
    border-bottom-left-radius: 3px;
    cursor: default;
}
  
.friendslist_entry_content {
    padding-top: 1%;
    font-size: 13px;
    line-height: normal;
    padding-left: 5px;
}
  
.playerAvatar.online img, .friend_status_online .friend_block_avatar img, .appHubIconHolder.online img, .avatar_block_status_online img {
  border-radius: 66px;
}
  
.playerAvatar.offline img, .friend_status_offline .friend_block_avatar img, .appHubIconHolder.offline img, .avatar_block_status_offline img {
    background: linear-gradient(to bottom, #515151 5%, #474747 95%) repeat scroll 0% 0% transparent;
  border-radius: 66px;
}
  
.playerAvatar.in-game img, .friend_status_in-game .friend_block_avatar img, .appHubIconHolder.in-game img, .avatar_block_status_in-game img {
    background: linear-gradient(to bottom, #66812E 5%, #59702B 95%) repeat scroll 0% 0% transparent;
  border-radius: 66px;
}
  
.chat_msg_area_highlight {
    height: 0px;
}
  
#chat_friendslist_area {
  border-radius: 66px;
}
  

}
