css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lazyfoo.net") {
  body {
    background-color: #231503;
    font-family: sans-serif;
  }
  
  div.tutPreface, div.tutText, div.tutFooter {
    background-color: #231503;
    width: 100%;
    border: none;
  }
  
  div.tutCode {
    /*background-color: #110A02;*/
    background-color: #222;
    width: unset;
    display: inline-block;
    border: 1px solid;
    font-family: monospace;
    box-shadow: 6px 6px 3px 0px #111;
    padding: 10px;
  }
  
  div.tutCodeHeader {
    background-color: #222;
    width: unset;
    border: 1px solid;
    font-family: monospace;
    box-shadow: 6px 6px 3px 0px #111;
    padding: 10px;
  }
  
  div.content {
    margin: 0 auto;
    max-width: 600px;
  }
  
  a.tutLink, a {
    color: #DD8F8F;
    text-decoration: none;
  }
  
  td.ToCTitle, td.artToCTitle {
    background-color: #5F3426;
    border: none;
  }
  
  td.tutLink, td.artLink {
    background-color: #42241A;
    border: none;
  }
  
  table.tutToC, table.artToC {
    border: 1px solid;
    box-shadow: 6px 6px 3px 0px #111;
  }
  
  img {
    border: 1px solid;
    box-shadow: 6px 6px 3px 0px #111;
  }
}
