css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("forums.whirlpool.net.au"),
domain("whirlpool.net.au"){

	#innerpage {
	filter: invert(80%);
	}
	body {
		font-size: 15px;
	}
	#forumindex h3 {
    background-color: #fff;
		color: #777;
	}
	#left.sidebar, #logo {
		filter: grayscale(1);
	}
	#page {
    background: #333;
    border-bottom: none;
	}
	#page #innerpage {
    background: #CCC;
	}
	#innershadow {
    background: linear-gradient(to bottom, #f1f1f1 0px, #EEE 10px, #CCC 100px);
	}
	#forumindex h3, #forumindex table, #content .topbar, #content .footbar, #replylist, #threads.pagefit table tbody, #profile #dataentry {
    box-shadow: 0px 2px 14px #fff;
	}
	#rightcol {
		display: none;
	}
	#leftcol {
    width: 100%;
	}
	#news .article {
    box-shadow: 0px 2px 14px #fff;
	}
	#left ul#menu > li.selected {
		background: #515151 !important;
	}
	#left ul#menu > li.selected > a {
    color: #777 !important;
    text-shadow: 0px 1px 0px #000;
		background: linear-gradient(175deg, #3F3F3F 0px, #515151 20px);
	}
	#left ul#menu > li.selected > ul > li, ul.breadcrumb, ul.breadcrumb li span, #left dl dd, .userstats, #leftcol #news .article h1 {
    color: #777 !important;
	}
	#content #replies.threadclosed .topbar, #content #replies.threadclosed .footbar {
    background-color: #B7D7D3;
	}
	body.brown #alert {
    background: linear-gradient(to bottom, #584245 0px, #48282C 100%);
    color: #777;
	}
	#threads.pagefit table thead tr td {
		color: #000;
	}
	#news .article.roundup h4 span.publisher {
    color: #6C8097;
	}
	#content .wcrep2, #left dl dt {
     color: #B58AB2 !important;
  }
	#left ul#menu > li > a, #left ul#menu > li.selected > ul > li > a {
    color: #CCC;
	}
	#left ul#menu > li.even, #left ul#menu > li.odd {
    background: linear-gradient(170deg, #3d3d3d 20px, #2d2d2d 50px);
	}
	#left ul#menu {
		background: #444;
	}
	#replylist div.reply {
    background: linear-gradient(to top, #f1f1f1 0px, #EEE 30px);
	}
	#replylist div.reply div.replymeta div.replytools, #replylist div.reply div.replymeta div.replyuser {
    background: linear-gradient(to bottom, #ddd 0px, #E6E6E6 30px);
	}
	#forumindex table td.title {
    background: linear-gradient(to bottom, #E6E6E6 0px, #F0F0F0 40px);
		border-bottom: 1px solid #fff;
	}
	#forumindex table td.threads {
    background: linear-gradient(to bottom, #E1E1E1 0px, #EBEBEB 40px);
		border-bottom: 1px solid #eee;
	}
	#threads table tbody tr.sticky td {
    background: linear-gradient(to bottom, #D8D8D8 0px, #E3E3E3 25px);
	}
	#threads table tbody tr.recent td {
    background: linear-gradient(to bottom, #D6D9E9 0px, #E6E8F1 25px);
	}
	#threads table tbody tr td {
		border-top: 1px solid #FFF;
    background: linear-gradient(to bottom, #E6E6E6 0px, #F0F0F0 25px);
	}
	#replylist div.notice {
    background: linear-gradient(to bottom, #E6E6E6 0px, #EEE 30px);
    color: #8ABEB7;
    border-bottom: 1px solid #BBB;
	}
	#replylist div.reply {
		border-bottom: 1px solid #FFF;
	}
	#threads table tbody tr td.title a.group {
    color: #aaa;
    background: linear-gradient(to bottom, #ddd 0px, #fff 25px);
	}
}
