css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https\:\/\/.*\.slack\.com\/(messages|threads|unreads).*") {
  /*
  primary: #1A1C1B
  secondary: #0B3136
  borders: #114374
  font: #4682B4
  link: #99d04a
  highlight: #ff876d
  */

  html,
  body
  {
    background: #1A1C1B;
  }

  /* Header */
  #client_header,
  #client_header .channel_header,
  #client_header #channel_topic_text,
  #client_header #search_terms
  {
    background: #1A1C1B;
  }
  #client_header #im_title,
  #client_header #im_title .subtle_silver,
  #client_header #channel_name,
  #client_header #channel_name .mpdm_member,
  #client_header #header_status_message,
  #client_header .channel_header_info_item,
  #client_header #channel_members_toggle_count,
  #client_header #pinned_item_count,
  #client_header #search_terms,
  #client_header .search_clear_icon .ts_icon_times_circle
  {
    color: #4682B4;
  }
  #client_header .channel_header_icon
  {
    color: #0B3136;
  }
  #client_header #header_search_form
  {
    border-color: #114374;
  }
  #client_header #header_search_form .icon_search
  {
    color: #4682B4;
  }

  /* Side Bar */
  .client_channels_list_container,
  #col_channels .p-channel_sidebar
  {
    background: #1A1C1B;
  }
  #team_menu #team_name,
  #team_menu #team_menu .notifications_menu_btn,
  #col_channels #col_channels_react_root .p-channel_sidebar__name,
  #col_channels #col_channels_react_root .p-channel_sidebar__section_heading
  {
    color: #4682B4;
  }
  #team_menu #team_menu_user_name
  {
    color: #4682B4 !important;
  }
  #col_channels #col_channels_react_root .p-channel_sidebar__channel--unread
  {
    color: #ff876d;
  }
  #col_channels #col_channels_react_root .c-scrollbar__bar
  {
    background: #4682B4;
  }
  #col_channels .p-channel_sidebar__banner,
  #col_channels .p-channel_sidebar__badge
  {
    background: #ff876d;
  }

  /* Content */
  #client-ui #client_body::before,
  #client_body .day_divider .day_divider_label,
  #client_body .action_hover_container .btn_msg_action,
  #client_body .monkey_scroll_bar_native_scrollbar_shim,
  #client_body #msg_input,
  #client_body #footer
  {
    background: #1A1C1B;
  }
  #client_body .message_body,
  #client_body .comment,
  #client_body #msgs_div .timestamp,
  #client_body .day_divider,
  #client_body #msg_input,
  #client_body #typing_text,
  #client_body #special_formatting_text,
  #client_body #msg_form .ts_icon_mentions,
  #client_body #msg_form #main_emo_menu,
  #client_body .action_hover_container .btn_msg_action
  {
    color: #4682B4;
  }
  #client_body .message_sender
  {
    color: #4682B4 !important;
  }
  #client_body .day_container .day_divider_label
  {
    border-top: 1px solid;
    border-bottom: 1px solid;
    border-color: #ff876d;
  }
  #client_body .day_container .day_msgs
  {
    border-top-color: #ff876d;
  }
  #client_body #msg_input,
  #client_body .action_hover_container
  {
    border-color: #114374;
  }

  #client_body ts-message:hover:not(.standalone):not(.multi_delete_mode):not(.highlight):not(.new_reply)
  {
    border-color: #114374 !important;
    background: #1A1C1B !important;
  }

  /* Menus */
  .menu ul li a,
  #autocomplete_menu.search_menu header,
  #autocomplete_menu.search_menu strong,
  #autocomplete_menu.search_menu .muted_text
  {
    color: #4682B4;
  }
  .menu,
  #menu .monkey_scroll_bar,
  .menu #monkey_scroll_wrapper_for_menu_items_scroller,
  .manu_submenu,
  #autocomplete_menu
  {
    background: #1A1C1B;
  }
  #autocomplete_menu.search_menu .time_modifiers::before
  {
    background: #0B3136;
  }
  #autocomplete_menu hr
  {
    border: 0;
  }

  /* Borders */
  #team_menu,
  #quick_switcher_btn,
  #primary_file_button,
  #message-input,
  .menu,
  .menu.submenu,
  .menu .divider,
  #search_autocomplete_popover {
    border-color: #114374 !important;
  }
  .day_divider hr {
    border: 1px solid #114374;
  }
  #team_menu {
    border-right: 1px solid;
    border-bottom-style: none;
  }
  #quick_switcher_btn {
    border-right: 1px solid;
    border-top-style: none;
  }

  /* Scrollbars */
  .monkey_scroll_handle_inner {
    background: #1A1C1B !important;
    border: 3px solid #114374 !important;
  }
  #monkey_scroll_wrapper_for_msgs_scroller_div .monkey_scroll_bar {
    background: #1A1C1B !important;
  }

  /* Highlights */
  .mention {
    background: #1A1C1B !important;
  }
  li.active a,
  ts-message:hover,
  #primary_file_button {
    background: #0B3136 !important;
  }
  .mention,
  #col_channels li.unread a span {
    color: #ff876d !important;
  }

  /* Fonts */
  @font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: 400;
    src: local('Open Sans'), local('OpenSans'), url(https://fonts.gstatic.com/s/opensans/v13/cJZKeOuBrn4kERxqtaUH3VtXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
  }
  @font-face {
    font-family: 'Inconsolata';
    font-style: normal;
    font-weight: 400;
    src: local('Inconsolata'), url(https://fonts.gstatic.com/s/inconsolata/v12/BjAYBlHtW3CJxDcjzrnZCI4P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
  }
  code,
  pre
  {
    background: black;
    border: 1px solid #114374;
    font-family: "Inconsolata", consolas, monospace !important;
    line-height: 1em !important;
  }
  code
  {
    font-size: 1.1em !important;
  }
  pre
  {
    font-size: 1em !important;
    color: #4682B4;
  }
  .p-channel_sidebar__list,
  .day_container,
  .message_body
  {
    font-family: 'OpenSans', sans-serif !important;
  }
  a:link,
  a:visited {
    color: #99d04a;
  }
}
