css:
@-moz-document domain("www.computerbase.de") {
/*make background grey*/
.block {
  background: #eee;
}
/*keep darker tint for stripes*/
.block-breakout, .block-highlight, .big-header {
  background: #ddd;
}
/*keep footer blue*/
.footer2 {
  background: #2c5b9c !important;
}

/*change headline to blue*/
.carousel-h3, .big-header__h1, .teaser__title, .slide__title {
  color: #2c5b9c !important;
}

/*put content to top*/
.block--ads, .legacy-ads {
  height: 0px;
}
.block-highlight {
  margin-top: 0px;
}

.article-view__footer {
    padding-bottom: 10px;
}
/*unify look of releated articles*/
.article-view__taglist, .related-box__ul {
  background: #eee;
}

/*disable share buttons*/
.share-link {
  display: none !important;
}

/*unify sidebars*/
.shoutbox-default {
  background-image: url("http://www.computerbase.de/img/shoutbox-default.svg") !important;
  background-repeat: no-repeat !important;
  border-top: 0 !important;
  background-position: 0.9375rem 0.8125rem !important;
}
.box--sidebar {
  background: #fff;
  border: 1px solid #bbb;
}
.box--sidebar h2 a {
  color: #2c5b9c;
}
/*new forum entries*/
.tabs-panel {
  display: block !important;            
}             
#panel-forum-ohne-antwort {
  display: block !important;
  margin-top: 20px;
}
.js-tabs .tabs {
  display: none;
}
.box--blue {
  background: #fff;
  border: 1px solid #bbb;
}
.box--blue .h2--forum-box a {
  color: #2c5b9c !important;
}
.box--blue a {
  color: #333 !important;
}
.box--blue time {
  color: #777 !important;
}



/*news list more compact*/
.ticker li {
  padding: .125rem 0;
}

/*colored logo*/
.homepage-link {
  background-image: url("http://abload.de/img/logo7zux1.png");
  background-repeat: no-repeat;
  background-position: 0em 0.3em;
  display: block;
  width: 147px;
  height: 64px;

}
.homepage-link img {
  display: none;
}

/*text in articles over full width*/
.text-width {
  max-width: 60rem;
}
.article-view__header {
  padding: 0 0;
  width: 805px;
}
.article-view__header figure {
  width: 100% !important;
}
.article-view__content .article-view__pic11 {
  left: 825px;
}

/*gallery button position*/
.gallery-prev--inline {
  left: 0px !important;
}
.gallery-next--inline {
  right: 0px !important;
}

/*article navigation*/
.tocbox {
  position: fixed;
  right: 50%;
  margin-top: 6.55em;
  margin-right: -44em;
  z-index: 2;
}
.toctoggle {
  display: none;
}
.toc--top {
  display: block !important;
}
.toc {
  border: 1px solid #bbb;
  box-shadow: none !important;
  border-top-left-radius: 0;
}


/********************/
/*Forum*/
/********************/

/*flat comments*/
.postbit, .postbitlegacy, .eventbit {
  padding: 0px;
}
.blockhead {
  box-shadow: none;
}
.toolsmenu {
  padding: 0px;
}
.toolsmenu-inner, .thread_controls-inner {
  background: #3572AF none;
}
.toolsmenu .popupgroup a.popupctrl, .toolsmenu .popupgroup .popupmenu a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover .popupctrl a.popupctrl.active {
  border-left: none;
}

/*flat buttons*/
.pagination span a {
  border-radius: 0px !important;
}
.blockfoot .button, .navpopupmenu .popupctrl, .navpopupmenu .textcontrol {
  background: #f4f4f4 none !important;
  border: 1px solid #cedfeb !important;
  color: #2c5b9c !important;
  font-weight: 500 !important;
}

/*remove optionsblock*/
.options_block_container {
  display: none !important;
}
.navlinks {
  border: none;
  background: #fff;
}

/*position of embedded forum*/
.vb-nav {
  margin-bottom: 0em;
  padding-bottom: 0.2em;
}
.below_body {
  margin-bottom: 20px;     
}
.block--top>.wrapper>.row {
  padding-bottom: 0px;
}
}
