css:
@-moz-document domain("reddit.com") {   

/*Removes Left rank*/
.rank {
  display:none !important}

/*Removes up/down arrows*/
.midcol{
  display:none !important}

/*Removes user karma*/
.userkarma{
    display:none !important}

/*Removes up/down votes*/  
.score, .upvotes, .downvotes {
      display:none !important}

/*Removes Karma Count*/ 
.titlebox .karma {
      display:none !important}

/*Removes Comment Score*/
.comment .score {
        display: none !important}

/*Removes Gold icon*/
.gilded-icon{
    display:none !important}

/*Top tab menu---> to remove Rising, Top, Gilded, Hot tabs*/
.tabmenu{
      display:none !important}

}
