css:
@-moz-document domain(youtube.com) {
  body{background-color:#222;color:#aaa;}
  body *{color:#777} /*default text color*/
  
  
  /*Header bar*/ 
  
  #masthead-positioner:hover #appbar-guide-button{background-color: #333;border-color:#133;}
  #yt-masthead-container{background-color:#111;border-bottom:solid 1px #133}
  
  #masthead-search-terms{background-color:#000;border:solid 1px #133;box-shadow:none}
  #search-btn, #upload-btn, #appbar-guide-button{color:#aaa; background-color:#333;border:solid 1px #133}
  
  
  /*Front page*/
  #masthead-appbar{background-color:#090909;border-bottom:solid 1px #133}
  .guide-pinned .guide-pinning-enabled #appbar-guide-menu {background-color:#090909;border-right:solid 1px #133}
  
  #appbar-guide-menu a{color:#aaa}
 
  
  #feed, .yt-card{background-color:#111; border: solid 1px #133; box-shadow:5px 5px 10px #000; }
  
  .branded-page-module-title-text{color:#aaa}
  .yt-ui-ellipsis{background:transparent}
  #browse-items-primary li,#browse-items-primary ul,.feed-header, .section-list li .item-section .branded-page-box{border-bottom-color:#133}
  .compact-shelf .yt-uix-button-shelf-slider-pager{border:none;background-color:#444}
  .browse-list-item-container:hover .compact-shelf .yt-uix-button-shelf-slider-pager{border:none}
  body #footer-container{background-color:#000;border-top-color:#133}
  .yt-uix-button{background-color:#111;color:#aaa;border:none} 
  #footer-main div, .yt-picker *{background-color:#000}

  
  /*video page*/
  .video-list .video-list-item .title{color:#777}
  .comments-view{background-color:#f11 !important}
  
  /*comments page*/
  #watch-response{background-color:#111}
  
  
}

/**comments panel (thanks panosbabo for the ids)**/
@-moz-document url-prefix(https://plus.googleapis.com),
regexp("https://apis\.google\.com/.*/widget/render/comments\?.*\&first_party_property=YOUTUBE.*"),
regexp("https://plus\.google\.com/.*/widget/render/comments\?.*"),
regexp("https://plus\.google\.com/.*/notifications/frame\?.*") {

.yJa, .uPa.BJa, .Mza.mj, .DJa, .tvb {background-color: #111;}
  div.Ct{color:#aaa} /*comment text*/
.Nza{background-color:#000;} /*share your thoughts*/
.Mga{border:none;} /*share your thoughts bubble arrow*/
}
