css:
@-moz-document domain("psim.us"), domain("play.pokemonshowdown.com") {
  body {
    background:url(http://static.comicvine.com/uploads/original/11113/111130700/3781498-2875034842-pokem.jpg) no-repeat !important;
    background-size:1800px;
    background-color:#000 !important;
  }
  
  .ps-room.ps-room-light.scrollable, .battleform label, .mainmenufooter a, .mainmenu .menugroup:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button, .roomlist a.ilink, .roomlist a.ilink small, .tabbar a.button.cur, .tabbar a.button, .pm-window h3, .pm-log, .chat-log,  .battle-log, .userlist li button, .popupmenu button, a, .chatbox label{
    font-family: helvetica,arial,sans-serif;
  }
  
  #header, .ps-room.ps-room-light.scrollable, .pm-window h3, .pm-window h3:hover, .pm-window.focused h3, .pm-window.focused h3:hover {
    background: rgba(0, 0, 0, 0.9);
  }
  
  .mainmenu .menugroup:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover, .pm-log-add, .popupmenu button:hover, .popupmenu button.sel:hover, button, .select, .battle-log-add, .battle-log h2 {
    background:rgba(0, 0, 0, 0.8);
  }
  
  .ps-room.ps-room-light.scrollable {
    color: #FFF;
  }
  
  .ps-room.ps-room-opaque {
    background:rgba(21, 21, 21, 0.9);
    color:#FFF;
  }
  
  .header .logo {
    opacity: 0;
  }
  
  .menugroup {
    background:rgba(0, 0, 0, 0);
  }
  
  .battleform label {
    background: url("http://i.imgur.com/yFWeHx0.png") no-repeat scroll left center transparent !important;
    padding: 4px;
    opacity:0.9;
    color: #FFF;
  }
  
  .mainmenufooter a {
    color:#FEE800;
    text-shadow: 0px 0px 5px #FEE800;
    text-decoration:none;
  }
  
  .mainmenufooter a:hover {
    color:#FFFFFF;
    text-shadow: 0px 0px 5px #FFFFFF;
    text-decoration:none;
  }
  
  .mainmenu .menugroup:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button {
    color:#FFF;
    font-size:11px;
    border: 1px solid #FFF;
  }
  
  .mainmenu .menugroup:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover {  
    border: 1px solid #B70304;
    color:#FFF;
  }
  
  .mainmenu .menugroup:nth-child(1) .button {
    font-size: 13px;
  }
  
  .roomlist a.ilink {
    color:#FEE800;
    text-shadow: 0px 0px 5px #FEE800;
    border: 1px solid #FFF;
    background:rgba(2, 24, 73, 0.8);
    box-shadow: none;
  }
  
  .roomlist a.ilink:hover {
    background:rgba(2, 24, 73, 0.8);
    color:#FFF;
    text-shadow: 0px 0px 5px #FFF;
    border: 1px solid #FFF;
  }
  
  .roomlist a.ilink small {
    color:#FFF;
    text-shadow:none;
  }
  
  .maintabbarbottom {
    background: #FFF;
    border: 1px solid #000;
    box-shadow: none;
  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    color: #000;
    background: #FFF;
    border: 1px solid #000;
    text-shadow: none;
  }
  
  .tabbar a.button {
    color: #000;
    background:#E8E8E9;
    border: 1px solid #000;
    text-shadow: none;
  }
  
  .tabbar a.button:hover {
    color: #000;
    background: #F6F6F6;
    border: 1px solid #000;
    text-shadow: none;
  }
  
  .broadcast-red, .broadcast-blue, .broadcast-green {   
    color: #000;
    border: 2px solid #000;
    padding: 5px;
    font-family: helvetica,arial,sans-serif;
    font-size: 10pt;
    text-shadow: none;
  }
  
  .broadcast-red {
    background: none repeat scroll 0% 0% #EC5C4D;
  }
  
  .broadcast-blue {
    background: none repeat scroll 0% 0% #8FA6B3;
  }
  
  .broadcast-green {
    background: none repeat scroll 0% 0% #9BB38F;
  }
  
  .pm-window h3 {
    border: 1px solid #FFF;
    color:#EBEBEB;
    font-size: 13px;
  }
  
  .pm-window h3:hover, .pm-window.focused h3, .pm-window.focused h3:hover {
   border: 1px solid #FFF;
    color:#FFF;
  }
  
  .pm-log, .chat-log, .battle-log {
    background:rgba(21, 21, 21, 0.9);
    color:#FFF;
    text-shadow: 1px 1px #000;
  }
  
  .ps-popup {
    background:url(http://fc06.deviantart.net/fs71/f/2010/311/c/0/haunted_mansion_version_0_5_by_arkeis_pokemon-d31xa83.jpg) no-repeat;
    background-size:1000px;
    color:#FFF;
    text-shadow:none;
  }
  
  .popupmenu button {
    box-shadow: none;
    color: #FFF;
  }
  
  .popupmenu h3 {
    color:#FEE800;
  }
  
  a {
    color:#FEE800;
    text-shadow: none;
  }
  
  a:hover {
    color:#F9EC3B;
  }
  
  .userlist {
    background:rgba(15, 15, 15, 0.95);
    border-right:1px solid #191919;
    color:#FFF;
  }
  
  .chat-log-add {
    background:rgba(15, 15, 15, 0.95);
    border-top: 1px solid #191919;
    color:#FFF;
  }
  
  .userlist li button {
    text-shadow: 1px 1px #000;
  } 
  
  .userlist li button:hover {
    background:#101010;
  } 
  
  .ps-overlay {
    background: rgba(0, 0, 0, 0.3);
  }
  
  a.ilink {
    color: #FEE800;
  }
  
  a.ilink:hover {
    color: #F9EC3B;
  }
  
  .infobox {
    border: 1px solid #C3C3C3;
  }
  
  button {
    color:#FEE800;
    border: 1px solid #FEE800;
  }
  
  button:hover {
    color:#FFF;
    border: 1px solid #FFF;
  }
  
  .select {
    color:#FFF;
    box-shadow:none;
    border-top-left-radius: 0px;
  }
  
  .select:hover {
    color:#FFF;
    background: #000;
  }
  
  .select:disabled, .select:disabled:hover {
    border-color: #999;
    background: none repeat scroll 0% 0% rgba(220, 220, 220, 0.65);
    box-shadow: none;
  }
  
  .battle-log .chat strong {
    color: #FEE800;
  }
  
  .battle-log .rated strong {
    background:#000;
  }
  
  .battle-log .chat em {
    color: #FFF;
  }
  
  .message-pm {
    color: #FEE800;
  }
  
  .switchmenu button:hover, .movemenu button:hover {
    color:#000;
  }
  
  .closebutton:hover, .minimizebutton:hover {
    border: none;
  }
  
  .chatbox label {
    text-shadow:1px 1px #000;
  }

  .ps-room .battle-controls {
    background: transparent !important;
  }
}
