css:
@-moz-document regexp("http://.*.2ch.net/.*/read.cgi/.*/.*"), regexp("http://.*.bbspink.com/.*/.*/") {

/*body*/
body {
  background-color: #ffffff;
  color:#111111;
  font-size:105%;
}

/*font-family*/
* {
  font-family:'Lucida Grande','Hiragino Kaku Gothic ProN',
 Meiryo, sans-serif;
}

/*↓LINK↓*/
a:link {
  cursor: pointer !important;
  text-decoration: none;
}

a:hover {
  color:#e67e22 !important;
  text-decoration: underline;
}
a:visited {
  color: #609;
}
a:link,a:active {
  color: #1a0dab;
}
/*↑LINK↑*/

/*名前等サイズ*/
dl.thread dt {
font-size:95%;
}

/*タイトル2chリンク無効*/
a.textlinkplus[href="http://2ch.net"] {
    pointer-events:none; 
}

/*↓投稿関連↓*/
form[method="POST"], input[name="FROM"],input[name="mail"],textarea {
    min-height: 20px;
  padding: 19px;
  margin-bottom: 20px;
  background-color: #f5f5f5;
  border: 1px solid #e3e3e3;
  border-radius: 4px;
  -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,0.05);
  box-shadow: inset 0 1px 1px rgba(0,0,0,0.05);
}
  
  
  
form[method="POST"] {
  width:800px;
}

input[name="FROM"],input[name="mail"] {
  height:5px;
  background-color:#ffffff;

}


textarea {
  height:175px;
  width:700px;
  font-size:105%;
  background-color:#ffffff;
}

input[type="submit"],input[type="button"] {
  color: #fff;
  background-color: #337ab7;
  border-color: #2e6da4;
  margin-bottom: 0;
  font-weight: normal;
  text-align: center;
  vertical-align: middle;
  cursor: pointer;
  border: 1px solid transparent;
  white-space: nowrap;
  padding: 6px 12px;
  line-height: 1.7;
  border-radius: 4px;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
  margin: 0px 5px;
}

div[style="background-color:red;color:white;line-height:3em;margin:1px;padding:1px;"] {
  background-color:red;
	border-radius: 0.25em;	
}
}

@-moz-document regexp("http://.*.2ch.net/.*/read.cgi/.*/.*"), regexp("http://.*.bbspink.com/.*/.*/") {
/*名前欄*/
/*sage*/

dl.thread dt a[href="mailto:sage"] {
  pointer-events:none; 
}
}
