css:
@-moz-document url-prefix(https://docs.google.com/)
{
/* Background image */
#kix-appview
{
    background-color: #333 !important;
    background-image: url('http://media.giphy.com/media/C3CUJPTRqNk88/giphy.gif') !important;
    border: 0 !important;
    outline: 0 none !important;
}

/* Sheet texture */
.kix-page.kix-page-compact-first
{
    background-color: transparent !important;
}
.kix-paginateddocumentplugin.kix-paginateddocumentplugin-compact-mode
{
    border: none !important;
}
.kix-page.kix-page-paginated,
.kix-page-content-wrapper 
{
    background-image: url('http://media.giphy.com/media/OXD2LVqRxXHYQ/giphy.gif') !important;
    background-size: 818px Auto !important;
    background-repeat: repeat !important;
  opacity: 1;
}

/* Comment section */
.kix-commentoverlayrenderer-normal
{
    opacity: .1 !important;
}
.kix-commentoverlayrenderer-highlighted
{
    background-color: #ffe168 !important;
    opacity: .4 !important;
}
.docos-anchoreddocoview
{
    opacity: .1 !important;
}
.docos-anchoreddocoview:hover,
.docos-docoview-active.docos-anchoreddocoview:hover,
.docos-docoview-active.docos-anchoreddocoview
{
    opacity: .9 !important;
}

div.docs-ui-unprintable:last-child
{
    background-color: transparent !important;
}
#docs-chrome
{
    width: 100% !important;
    z-index: 100; 
    position: fixed !important;
    margin-top: 0px !important;
    -moz-transition: all 0.8s;     
    -webkit-transition: all 0.8s;
    opacity: 0.0 !important;
}

#docs-chrome:hover
{
    margin-top: 0 !important;
    opacity: 1 !important;
    -moz-transition: all 0.4s;     
    -webkit-transition: all 0.4s;
}

::-webkit-scrollbar-thumb
{
     -webkit-transition: background-color 0.3s;
}

iframe
{
     border-top: none !important;
}
}
