css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("erez.the-class.co.il") 
  {
    #header, #footer
     {
       background: url('http://subtlepatterns.com/patterns/footer_lodyas.png');
       color: #FEFEFE;
     }
    
    .sub-menu
     {
       margin-right: -150px !important;
     }
    
    #content
     {
       font-family: Tahoma !important;
     }
    
    .logo-place
     {
       margin-top: 20px;
     }
    
    .logo-place h1 span
     {
       font-family: Tahoma !important;
       font-weight: lighter;
       color: #22a7f0 !important;
     }
    
    .logo-place h1 + p span
     {
       font-family: Tahoma !important;
       color: #EEE !important;
     }
    
    #sidebar div[style = "height: 63px;width: 100%;"], .textwidget
     {
       display: none !important;
     }
    
    div#sidebar h4
      {
        color: #22a7f0;
        font-size: 20px;
      }
    
    label.screen-reader-text
     {
       font-size: 15px;
       font-family: Tahoma;
     }
    
    #searchform
     {
       margin-top: -20px;
       margin-bottom: 40px;
     }
    
    #searchform input#s, #searchsubmit
     {
       background: #DDD;
       border: none;
       border-bottom: 3px solid #22a7f0;
       padding: 5px;
       font-family: Tahoma;
       border-radius: 3px;
       color: #444;
     }
    
    #searchsubmit
     {
       margin-top: 10px;
       margin-left: 11px;
       float: left;
     }
    
    h4 span#LoginWithAjax_Title
     {
       font-weight: 100 !important;
       font-size: 20px !important;
     }
    
    #box, #LoginWithAjax
     {
       border-bottom: 3px solid #22a7f0;
       border-radius: 5px;
       color: #444;
       background: #DDD;
     }
    
    #LoginWithAjax a
     {
       color: #22a7f0 !important;
       transition: all 0.5s;
     }
    
    #LoginWithAjax a:hover
      {
        text-decoration: underline;
      }
    
    div#sidebar h4 + script + div
     {
       margin-top: -15px !important;
     }
    
    #header
     {
       border-bottom: 3px solid #22a7f0;
     }
    
    #footer
     {
       border-top: 3px solid #22a7f0;
       font-family: Tahoma;
     }
    
    #footer a
     {
       display: none;
     }
    
    #footer:after
      {
        content: '©EvyatarDa';
      }
  }
