css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.investing.com") {

  /*Remove top Barr*/
  [class="topBar"] { display: none !important; }
  
  /*Remove top Menu*/
  DIV[id="navBar"] { display: none !important; }  

  /*Remove Content Left*/
 [id="leftColumn"] { display: none !important; }
  
  /*Remove Quotes Indices*/
  DIV[id="Quotes"] { display: none !important; }  
  
  /*Remove Recent Quotes*/
  DIV[id="user_last_quotes"] { display: none !important; }
  
  /*Remove Connect to Investing.com*/
  DIV[id="Side_Block_Facebook"] { display: none !important; }
  
  /*Remove Footer*/
  [id="footerWrapper"] { display: none !important; }
  
.wrapper {
    border-bottom: 0 none;
    border-top: 0 none;
    margin: 0 auto;
    padding: 51px 0 0;
    position: relative;
    width: 1400px;}
  
  HTML.com .iosTakeover {
    background-image: url("http://glocdn.investing.com/takeover/ios_bg/takeover_EN2.jpg");}
}
