@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rust-lang.org") {
  body, #page-wrapper, #toc {
    background-color: #2B2D2D !important;
    color: #a8a8a8 !important;
  }
  
  #active-code, #editor {
    background-color: #2d2e2b !important;
  }
  
  p {
    color: #BEBEBE !important;
  }

  .btn-default {
    color: black !important;
  }  

  a {
    color: white !important;
  }
  
  .block a:hover {
    color: black !important;
  }
  
  .docblock code {
    background-color: #373737 !important;
  }
    
  h1 {
    color: #E9E9E9;
  }
  
  .container .menu h2, h2 {
    color: #D4D4D4;
  }
  
  h3 {
    color: #BEBEBE !important;
  }
  
  .content .in-band {
    color: #a8a8a8 !important;
  }
  
  .location a {
    color: black !important;
  }

  :target {
    background: #4271AE !important;
  }

  .post-content pre, .post-content code {
    background: none !important;
  }

  /* Forum stuff */

  .topic-list {
    background: rgba(255,255,255,0.1) !important;
  }

  .badge-wrapper.bar span.badge-category {
    color: white !important;
  }

  .topic-map {
    background: none !important;
  }

  .moderator .topic-body {
    background: #4271AE !important;
  }

  aside.onebox {
    background: rgba(255,255,255,0.1) !important;
  }

  .d-header {
    background: #404242 !important;
  }

  aside.quote .title, aside.quote blockquote {
    background: rgba(255,255,255,0.1) !important;
  }

  /* inline code blocks - selector group is completed by the theme file */

  pre, .markdown-format pre, .markdown-body pre, .markdown-format .highlight pre, .markdown-body .highlight pre,

  body.blog pre, #facebox pre, .blob-code-context, .blob-num-context, .blob-expanded, .terminal, .copyable-terminal,


