css:
@-moz-document domain("kronos.mcanime.net") {
body {
    background-color: #000 !important;
	background-image:url('/*[[wall]]*/') !important;
	background: #333 no-repeat fixed top center;
	background-position:50% 0px !important; left: 30px;
}

#tab-menu, #iconbar, .tip_left, .lines {
	display: none !important;
}

/* FONDO 1 */
#content, #sidebar > div, #sidebar > fieldset, #tweet-detail, #pComment, #tab-menu ul, #fboard > li.selected, #fboard, #content-enc, #downloadBox .wrapper div, #last-post, #general_info {
	background: rgba(0,0,20,.6) !important;
	color: #afafaf !important;
	border: 0px !important;
  	border-radius:0px !important;
	box-shadow:0 0 10px 0 rgba(0,0,20,.4) !important;
}

textarea, #gtabs > a.selected, input, select, dt, .statistics {
	color: rgba(256,256,256,.9) !important;
	background: rgba(20,20,20,.6) !Important;
    border: solid 1px rgba(256,256,256,.3) !important;
    box-shadow: 0px 0px 0px #E1E1E1 inset !important;
}

/* FONDO 2 */
.pComment-more, .pComment-content, #sidebar h4, .profile-actions > a, .sil-row, .friends-info h5, .pComment-item, .more-feeds, #tab-menu ul > li, .item.odd, div.list div.item.header, .profile-info-block, .ps-title, .comments_wrap, .divs, .topics, .preview .post, #topicreview .post, #popular-series, #last-post > div, #general_info > div, .ur-item {
	color: rgba(256,256,256,.7) !important;
	background: rgba(0,0,0,.3) !Important;
	/*box-shadow:0 0 10px 0 rgba(256,256,256,.1);*/
	padding: 2px 4px !important;
    border: solid 1px rgba(0,0,0,.2) !important;
    margin-top: 1px !important;
  	border-radius:0px !important;
}
  
blockquote, .tabs > a, dd, h3.header_title, .gridbox > li, .codebox {
	color: rgba(256,256,256,.7) !important;
	background: rgba(30,30,40,.5) !Important;
	/*box-shadow:0 0 10px 0 rgba(256,256,256,.1);*/
    border: solid 1px rgba(200,230,256,.2) !important;
    margin-top: 1px !important;
  	border-radius:0px !important;
}

.highlight, .advertisement {
	color: rgba(256,256,256,.7) !important;
	background: rgba(250,250,256,.3) !Important;
	box-shadow:0 0 3px 0 rgba(256,256,256,.7) !important;
    border: 0 !important;
    margin-top: 1px !important;
  	border-radius:0px !important;
}

.user img, .info, .pComment-pic img {
	background: rgba(30,30,40,.8) !Important;
    border: solid 1px rgba(200,230,256,.2) !important;
}

.story-header {
	color: rgba(256,256,256,.8) !important;
	background: rgba(0,0,0,.3) !Important;
	/*box-shadow:0 0 10px 0 rgba(256,256,256,.1);*/
	padding: 2px 4px !important;
    border: solid 1px rgba(0,0,0,.2) !important;
  	border-radius:0px !important;
}
.pComment-content {
	margin-left: 37px !important;
}

.story-header {
	width: 561px !important;
}
  
/* FONDO TRANSPARENTE */
.pComment-item, .profile-story, #pComment ul.pComment-list, #pComment > form > div.crumbs, #pComment > form > div.add-pComment, .series-info h5, .friends-grid, .more-stories, #sub-gtabs, #gtabs, #gtabs > a, .list > .item, #content > h3, .post_wrap, .signature, .top_panel, #fixedblock, .last-message, #quickreply, form, #postform h3, .preview, #topicreview, hr, #content-enc > h3 > span.title, #block-estrenos, #downloadBox > div > a, .ur-wrap {
  	background: transparent !important;
  	border: 0px !important;
}  

.shadow, .info ul > li {
	border-bottom: 1px solid rgba(200,230,256,.2) !important;
    border-top: 0 !important;
      }

  .profile-story {
	min-height: 80px !important;
}  
  
/* TEXTO */
#content a, .cs-body a, .pComment-content > a, .series-info a, .friends-info a, #tab-menu .selected a, .comments_wrap, cite, .profile a, .post-date a, .f-descrip h4 a, label, strong a, .highlight a, .gridbox li span, h4, #general_info a, .statistics span, .advertisement a {
	color: rgba(200,230,256,.8) !important;
  }
  
#content span, .cs-body span, .series-info h5, #content h3, .comments_wrap > div, .post-date, #postform h3, .message h3, #general_info li {
	color: rgba(256,256,256,.8) !important;
}

/* IMAGEN */

.story-message > img {
	max-width: 560px !important;
	height: auto !important;
}
  
object[data*="youtube"] {
  	width: 560px !important;
  	height: 317px !important;
}

.story-pic img, #profile_pic {
	width: 56px !important;
	height: auto !important;
}

.post .comments img, .scontent img {
max-width: 100% !important;
}

/* Menu bar */
#top {
  	background: rgba(0,0,20,.6); border:none; width:100%; position:fixed; z-index:1; padding-top:-4px; height:28px;z-index:5;
	box-shadow:0 0 10px 0 rgba(0,0,20,.4) !important;
}

#wrapper #top {
  	margin-top:-28px;z-index:5
}

#userbar h1 {
  	margin-top:-1px;z-index:5
}

#main-menu, #user-actions {
  	margin-top:2px;z-index:5
}

#wrapper {
  	padding-top:32px;z-index:5
}

#userbar div.ua-box {
	background:rgba(0,0,0,.8) !Important;
	border:1px solid #5b6371 !important;
	border-radius:0 !important;
	margin-top:2px !important;
	color:#d6d6d6 !important;
}

#userbar #main-menu div.ua-box {
	margin-top:3px !Important;
}

#userbar div.ua-box a {
	color:#FAFAFA !important;
	transition:all 150ms ease-in-out;
}

#userbar div.ua-box a:hover {
	background:rgba(235,235,256,.3) !important;
	box-shadow:0 0 10px 0 rgba(235,235,256,.3);
	transition:all 150ms ease-in-out;
}

#userbar div.ua-box hr {
	border-top:1px dashed #9c9c9c !important;
	position:relative !Important;
	left:1px !important;
	margin:0 auto 0 auto !important;
}

#userbar div.ua-box h4 {
	padding-bottom:1px !Important;
	border-bottom:1px dashed #9c9c9c !important;
}

#userbar div.ua-box .ua-block {
	padding:4px 0px !important;
}

#userbar div.ua-box .ua-block.view-all a.user-notification {
	margin:0 0 -4px 0 !important;
	padding:3px 0 5px 0 !important;
}

.actions > .edit {
	background: url("http://i.imgur.com/u5ddCe5.png") !important;
	background-repeat: no-repeat !important;
	width: 57px !important;
	height: 17px !important;
}
.actions > .quote {
	background: url("http://i.imgur.com/TMV9W9E.png") !important;
	background-repeat: no-repeat !important;
	width: 46px !important;
	height: 17px !important;
}
.actions > .warn {
	background: url("http://i.imgur.com/MzNdL7r.png") !important;
	background-repeat: no-repeat !important;
	width: 78px !important;
	height: 17px !important;
}

.actions > .delet {
	background: url("http://i.imgur.com/64r1Blp.png") !important;
	background-repeat: no-repeat !important;
	width: 62px !important;
	height: 17px !important;
}

.actions > a {
	opacity: 0.6;
    transition: all 200ms ease-in-out;
}

.actions > a:hover {
    opacity: 1 !important;
    transition: all 200ms ease-in-out;
}
}
