    border:none;
    box-shadow: none;
    text-shadow: none;
    color: #000;
  }
  
  .maintabbarbottom {
    box-shadow: 0 4px 3px -2px #202020;
    position:absolute;
    z-index: 1;
  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    background:#FFF;
    border-radius: 0px;
    
  }
  
  .tabbar a.button {
    background:#FFF;
    border-radius: 0px;
    font-size: 9px;
    margin-left: 10px;
  }
  
  .tabbar a.button:hover {
    background: #FFF;
  }
  
  .tabbar li:last-child a.button {
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
  }
  
  .tabbar li:first-child a.button {
