    border-top-left-radius: 0px;
  }
  
  .tabbar a.button i {
    font-size: 13px;
  }
  
  .roomlist a.ilink {
    color:#EFEEB6;
    border-radius: 0px;
    border:1px solid #EFEEB6;
    text-shadow:none;
    box-shadow:none;
    background:rgba(67, 94, 124, 0.9);
  }
  
  .roomlist a.ilink small {
    color:#EFEEB6;
    text-shadow: none;
  }
  
  .roomlist a.ilink:hover {
