    border-radius: 0px;
  }
  
  .userlist {
    border-right: none;
  }
  
  .chat-log-add {
    border-top: none;
  }
  
  .chat-log {
    border-bottom: 1px solid #A8A8A8;
    border-left: 1px solid #A8A8A8;
  }
  
