    color:#435E7B;
  }
  
  .closebutton:hover {
    color:#FF695E;
  }
  
  .minimizebutton:hover {
    color:#FFD379;
  }
  
  .message-error {
    color: #B94545;
  }
  
  .ps-popup {
    background:url(http://i.imgur.com/Q8vHT0Y.png);
  }
  
  
  
  
  /* Teambuilder CSS */
  
  .setmenu button {
    color:#FFF;
  }
  
  .setmenu button:hover {
    color:#FFF;
    background:rgba(0, 0, 0, 0.44);
  }
   
  .utilichart .movenamecol {
    padding-top: 5px;
    height: 23px;
    width: 152px;
    color: #FFF;
    font-size: 10pt;
  }
  
  .utilichart .namecol {
    padding-top: 5px;
    height: 23px;
    width: 120px;
    color: #FFF;
    font-size: 10pt;
  }
  
  .utilichart .pokemonnamecol {
    padding-top: 5px;
    height: 23px;
    width: 127px;
    color: #FFF;
    font-size: 10pt;
  }
  
  .teambuilder-results .result a:hover {
    background:rgba(0, 0, 0, 0.44);
  }
  
  .utilichart .abilitycol, .utilichart .twoabilitycol, .utilichart .statcol, .utilichart .labelcol, .utilichart .widelabelcol, .utilichart .pplabelcol {
    color:#727272;
  }
  
  .teambuilder-results h3, .teambuilder-results .utilichart h3 {
    color:#000;
    text-shadow:none;
  }

  
  
  
  
  /* Battle CSS */
  
  
  
  
  
  .battle-log-add {
    border-color:#3e393a;
    background:url(http://i.imgur.com/urdZ7Vg.png); 
    text-shadow:1px 1px black
  }

  .battle-log {
      background:url(http://i.imgur.com/hsqGAFE.png); 
      color:#151515; 
      border-width:0
  }

  .battle-log h2{
      position:relative; 
      border:0px solid #AAAAAA; 
      background:url(http://i.imgur.com/6MygRUw.png)
  }

  .battle-log h2:after{
      display:block; 
      content:""; 
      width:31px; 
      height:18px; 
      position:absolute; 
      right:5px; 
      top:5px
  }

  .battle-log .chat em{
      padding:1px 4px 1px 3px; 
      color:#ecf0f1; 
      font-style:normal
  }

  .battle-log .chat strong{
      color:#ecf0f1
  }

  .battle-log .chat small{
      color:#ecf0f1
  }

  .ps-room.ps-room-opaque{
      background:url(http://i.imgur.com/I03iboW.png); 
