  .whatdo strong{
    font-weight:normal;
    font-size:11.5pt!important;   
  }

  .healthy{
    float:right; 
    color:#b4b4b4;
    position:relative; 
    top:1px;
    opacity:0;     
    right:-350px
  }  

  .weak{
    float:right; 
    position:relative; 
    top:1px; 
    opacity:0; 
    right:-350px
  }    

  .critical{
    float:right; 
    position:relative; 
    top:1px; 
    opacity:0; 
    right:-350px
  }   

  .weather{
