  }

  .rainweather, .raindanceweather{
      background:url(http://i.imgur.com/hic1nf9.gif) no-repeat scroll left top; 
      opacity:0.5;
      background-size:cover
  }

  .sandstormweather{
      background:url(http://i.imgur.com/qOw9a6v.gif) no-repeat scroll left top; 
      opacity:0.5; 
      background-size:cover
  }

  .hailweather{
      background:url(http://i.imgur.com/eUqkcVW.gif) no-repeat scroll left top; 
      opacity:0.5; 
      background-size:cover
  }

  .pseudoweather{
      background:url(http://i.imgur.com/WCrYols.gif) no-repeat scroll left top;     
      opacity:0.5; 
      background-size:cover; 
      width:640px; 
      height:360px
  }

  .switchmenu{
      width: 430px;    
      left: 102px;    
      top: -35px;    
      position:relative;   
      padding-bottom:135px;    
      background:url(http://i.imgur.com/zkXf6PL.png);    
  }

  .switchmenu button, .switchmenu button:disabled{
      width:137px;   
      height:48px; 
      background:url(http://i.imgur.com/WMic5PP.png) !important; 
      border:none; 
      box-shadow:none; 
      border-radius:0; 
      font-size:10pt
  }

  .switchmenu button{   
      transition:All 1.6116s ease;    
      color:black;     
      text-align: left;    
      text-shadow:1px 1px grey
