  }

  .movemenu button.type-Fairy:hover{
      transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px);
      background:url(http://i.imgur.com/bxU4lIL.png)
  }

  .movemenu button.type-Fire{
      background:url(http://i.imgur.com/aP39wjQ.png)
