css:
@-moz-document domain("diasp.eu" ) {

html,body, #login {
    background: #E8F8E8 url('https://wallpaperscraft.com/image/beach_sand_island_48641_3840x2160.jpg')no-repeat fixed left bottom;

    background-size: 100%;
top: 50%;
}
   
a:link {
    color: #008A8A;
}
    
#banner, #steps, #links {
    background-color: rgba(255,255,255,0.4) !important;
    border-radius: 15px !important;
}
    
#huge-text {
    color: #008A8A !important;
} 
         
 span a:link, #user_menu a:link, #btn-default {
    color: #008A8A;
}
  
.disabled, .btn, .button, #login-link {
    color: black !important;
}
    
.btn-primary {
    color: white !important;
}
    
*[id*='_dropdown'] {
    background-color: #E8F8E8 !important;
}
    
*[id*='_dropdown'] a:link, .hoverable a:link, .all_aspects a:link, a[data-stream='followed_tags'] {
    color: #008A8A !important;
}
    
.conversation_participants {
    background-color: #E8F8E8 !important;
}
    
#aspect_stream_container {
    background-color: rgba(255,255,255,0.3);/*#e8f8e8;*/
    border-right: 1px #939393 solid;
    border-bottom: 1px #939393 solid;
    border-left: 1px #939393 solid;
    border-radius: 15px;
}
    
.control-group, .control-label, #publisher_textarea_wrapper, #status_message_fake_text, textarea[id*='comment_text_on_' ], input[id*='user'], input[id*='profile'], .as-selections, #profile_bio, select[id*='profile_date_'], select[id*='user'], #message_text, #bitcoin_address, #dogecoin_address {
    background-color: #E8F8E8 !important;
    border-width: 1px;
}
    
#stream_container {
    background-color: #E8F8E8;
}
    
#stream_selection, #profile, #aspect_nav, #real-post-content, #conversation_inbox, #faq_nav {
    background: rgba(208,240,208,0.8);
    border-width: 1px;
    border-radius: 1px 15px 1px 15px;
    border: 5px solid #E0E0E0;
    padding: 10px 10px 10px 10px;
}
 
.profile_photo img {
    height: auto;
    width: 200px;
   border-radius: 1px 15px 1px 15px;
    border: 2px solid #E0E0E0;
}
.avatar {
    background-color: #EEE;
    width: 50px;
    height: 50px;
    overflow: hidden;
    border-radius: 10px;
    border: 2px solid #E0E0E0;
}
a img {
    border: medium none;
    border-radius: 10px;
    border: 2px solid #E0E0E0;
}

#profile .image_list img {
    height: 50px;
    width: 50px;
    border-radius: 10px;
    border: 2px solid #E0E0E0;
}
  
}
