css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://(safe\.)?www\.(haosou|so)\.com/s\?.*") {
/*布局拉宽*/
div#container > div#main {width:92%!important;float:none!important;}
div#warper{margin-top: 30px !important;}
div#main > ul#m-result > li {width:auto!important;}

/*为您推荐*/
div#main > div#so_top {background-color: #F5F7FA!important;padding:8px 10px!important;margin:0px!important;border-bottom:solid 1px #ccc;}

/*底部分页，搜索框，相关搜索对齐*/
div#page {height:50px!important;}
div#page, div#search {margin-left: 2.8%!important;}
div.mod-relation {margin-left: 4.3%!important;width:90.8%!important;}

/*外框美化*/
div#container > div#main {border: 1px solid #ccc;padding:0!important;margin:0 35px!important;}

/*奇数行美化*/
div#main > ul#m-result > li:nth-child(even) {background-color: #EAEBEC!important;padding:8px 10px!important;margin:0px!important;border-bottom:solid 1px #ccc;}

/*偶数行美化*/
div#main > ul#m-result > li:nth-child(odd) {background-color: #F5F7FA!important;padding:8px 10px!important;margin:0px!important;border-bottom:solid 1px #ccc;}

/*搜索结果优化*/
div.res-comm-img {width:auto !important;float:left!important;}
div.res-comm-con {width:88% !important;float:right!important;}

/*顶部相关搜索*/
div#main > div#rs-top {width:auto!important;background-color: #F5F7FA!important;padding:8px 10px!important;margin:0px!important;border-bottom:solid 1px #ccc;}

/*顶部仍然搜索*/
div#main > div.m-corr {width:auto!important;background-color: #F5F7FA!important;padding:8px 10px!important;margin:0px!important;border-bottom:solid 1px #ccc;}

/*搜索结果悬停美化*/
div#main > ul#m-result > li:hover {background-color: #B5E0F5!important;}

}
