css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("chess.com") {

/* Blocks ads and unwanted stuff */
.adzone,.webzone, .admeta, #footerbar, #topflinks, #topflinks, #bottomflinks,#advert_container, .cpremium, .premium, #add2timeline, #footer, #ad_report_host_no263,#ad_report_host_no166.webzone,#div-gpt-ad-1406590358007-2 {display: none !important}


    
  /* board*/
    
    .boardDummy .playername a, .boardDummy .playername a:visited, .boardDummy .timerin, .boardDummy .timerin.active { color:blue; }
    .bs {
    padding: 5px 10px;
    background-image: url("http://s3.postimg.cc/4xq6qmnrn/Background1752_1168.gif")!important;
    border-radius: 5px;
    margin: 0px auto;}
    
 /*LiveChess Boards later*/
    

/* Navigation */
#nav { background: #004600 !important; }
#nav li { border-right: 1px solid #002000 !important; }
#nav li li { border: none !important; }
#nav li a { color: white !important; }
#nav li a:hover { color: #002000 !important; }
#nav li ul a { color: #004600 !important; }
#nav li.home { border-left: 1px solid #002000 !important; }

/* Other */
body { background: #002000 !important; }
/*LiveChess*/
 /* div#main_bc {background-image: url("http://s3.postimg.cc/4xq6qmnrn/Background1752_1168.gif")  !important;}*/
    
    
    body, #main_bc,html,#main_bc_header {
  width:100%;
  height:100%;
  background-color:blue;
  background-image:url("http://s6.postimg.cc/rif81o0xt/008_paper_4000x4000_all_free_download_com.jpg")!important;
  font-size:13px;
}
 

.button-submit, .button, .button-cancel small { background-image: -moz-linear-gradient(0% 200% 90deg, blue, red);background-color: red !important; }
 .button-submit:hover {
    background-image: -moz-linear-gradient(200% 100% 90deg, yellow, green);
    background-color: yellow;
    box-shadow: 1px 1px 0px green inset;
}
.button-submit:hover {
    color: blue;
}
/*Timers*/
    
 
   
    

.timerin { color:#777 !important;font-size-adjust: 0.58;font-family: Tahoma; }
.timerin.active { color:#777 !important;font-size-adjust: 0.58;font-family: Tahoma; }

.sidebar a { color: mocassin !important; }

.playername a, .title, .you, .you span, .gameboard-header a, .gameboard-desc a, .player a, .font15, li a, #c22 a, .playerrating { color: #508050 !important; }
h4 a { color: #004600 !important; }

}
