css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('torrentz.eu'),domain('torrentz.in')
{
body{
  width:100%;
}
div.top{
  background:#24A49F ;  
  border-radius:0px;
}
div.top > ul{
  float:none;
  margin-left:35%;
}  
div.top > ul > li > a {
  background:#24A49F
}
div.top > ul > li > a:hover {
  border-bottom:none;
  border-radius:0px;;
  background:#37BCBC;  
}  
div.top > ul > li{
  border:none;
}  
form.search{
  border-radius:0px;
  width:800px;
  height:70px;
  margin:200px auto;
  background:none;
} 
form.search #thesearchbox{
  height:58px;
  background:#24A49F;
  color:white;
  font-size:40px;
  border-radius:0px;
}  
form.search #thesearchbutton{
  height:65px;
  border-radius:0px;
  color:white;
  background:#24A49F;
  font-weight:normal;
  font-size:41px;
}  
form.search #thesearchbutton:hover{
  background:#37BCBC;
}  
p.generic{
  display:none;
}
div.results{
  background:#24A49F;
  border-radius:0px;
  margin:20px 10px;
}  
div.results > h2{
  color:white;
} 
div.results > div{
  color:white;
}
div.results > div > a{
  color:white;
}
div.results > div > b{
  color:black;
} 
body a:hover, div.results a:hover, div.trackers a:hover, div.index-stats a:hover{
  color:black;
  text-decoration:none;
} 
div.results adl{
  border-bottom:none;
}  
div.note{
  display:none;
}  
ul.relatedq{
  display:none;
}  
div.results > p > span > a:hover{
  color:black;
  background:#24A49F;
} 
div.results > p > span > span{
  border:none;
  border-radius:0px;
  color:black;
  background:white;
}
div.results > p > span > a{
  background:white;
  color:black;
  border:none;
  border-radius:0px;
}
div.cloud > a{
  background:#24A49F;
  border: 1px solid #24A49F;
  border-bottom-left-radius: 20px;
  border-top-right-radius: 20px;
  border-top-left-radius: 5px;
  border-bottom-right-radius: 5px;
  text-decoration: none;
  line-height: 43px;
  padding: 2px 10px;
  margin: 10px 0px;
}
div.cloud > a:hover{
  background: white;
  color: #24A49F;
  border: 1px solid #24A49F;
}
div.download > h2 > span{
  color:black;
  margin:20px 8px;
}  
div.download > h2{
  color:white;
}
div.download > p{
  display:none;
}  
div.download{
  background:#24A49F;
  border-radius:0px;
} 
div.download > dl > dt > a > span.u{
  color:#24A49F;
  text-decoration:none;
} 
div.download > dl > dt > a > span.n{
  color:#24A49F;
}
div.download > dl > dt > a:hover{
  background:none;
}  
div.trackers{
  background:#24A49F;
  border-radius:0px;
} 
div.trackers > div{
  color:white;
}  
div.results > dl > dt > a{
  color:#24A49F
}
div.trackers > h2{
  color:white;
}
div.trackers > dl{
  border:none;
} 
div.trackers > dl > dt > a{
  color:#24A49F;
}
div.trackers > p{
  color:white;
  background:#24A49F;
}  
div.votebox > span.replist > a, div.votebox > span.replist > a.m{
  color:#24A49F;
  border:1px solid #24A49F;
  border-radius:0px;
}
div.votebox > span.replist > a:hover, div.votebox > span.replist > a.m{
  color:white;
  background:#24A49F;
  border:1px solid #24A49F;
}  
div.files{
  margin:20px 8px;
}
div.files > p{
  display:none;
}  
div.files{
  color:white;
  background:#24A49F;
}
div.files > div{
  color:white;
}
div.votebox > span.status{
  color:#24A49F;
  border:2px solid 24A49F;
} 
div.votebox > a.up, div.votebox > a.upm{
  color:#white;
  background:#24A49F;
  border:2px solid #24A49F;
}  
div.votebox > a.up:hover, div.votebox > a.upm{
  color:#24A49F;
  border:2px solid #24A49F;
  
}
div.files{
  border-radius:0px;
}
div.comments{
  border-radius:0px;
  background-color:#24A49F;
  margin:20px 8px;
}
form.profile > fieldset{
  border-radius:0px;
  background-color:white;
  border:none;
}

div.files > h2{
  color:white;
}
div.files li.t{
  color:black;
}  
div.footer{
  display:none;
}
  
}
