css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.fantabobshow.com") {
  /* Largeur de page */
  .pageWidth 
  {width: 1200px}
  
  /* Unread icon */  
  .node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon
  {background-image: url("http://www.pixenli.com/images/1426/1426883374087679400.png");}
  
  /* Read icon */
  
  .node .nodeIcon
  {background: none no-repeat scroll center center transparent;
  margin: 10px 0px 10px 4px;
  float: left;
  width: 44px;
  height: 34px;}
  
  /* Barre Acceuil > Forum */
  .breadcrumb
  {margin-top: 0px;}
  
  /* Sidebar: positionnement */
  .sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3
  {display: none;}
  
  /* Sidebar: contenu */
  .sidebar .primaryContent, .sidebar .secondaryContent
  {background: rgba(255,255,255,.8);
  padding: 15px 15px 15px;}
  
  /* Accueil - Forum - Shoutbox etc */
  .navTabs .publicTabs .navLink
  {padding: 0px 75px;}
  
  /* Marquer ts les forums comme lus etc */
  .secondaryBar
  {height: 43px;
    background: rgba(0,0,0,.5) }
  
  /* Page sélectionnée */
  .navTabs .navTab.selected .navLink
  {background: url("http://www.pixenli.com/images/1426/1426881727059295200.png") repeat-x scroll center;
  color: #282828;
  text-shadow: 0px 1px 0px rgba(255, 255, 255, 0.3);}
  
  .navTabs .navTab.PopupClosed .navLink:hover
  {background: url("http://www.pixenli.com/images/1426/1426888566017202500.png") repeat-x scroll top;
  color: rgb(240,240,240);
  text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.3);}
  
  /* Contenu en bas de page */
  .footer .pageContent
  {display: none;}
  
  /* Réduire la barre noire en bas */
  .footerLegal .pageContent
  {display:  none}
  
  /* Bannière */
  #header
  {background: url("http://www.pixenli.com/images/1426/1426877902014093000.png") repeat fixed 0% 0%, url("http://www.pixenli.com/images/1426/1426869816045963900.jpg") repeat fixed;}
    
  /* Fond du forum */
  #content
  {background: url("http://www.pixenli.com/images/1426/1426869816045963900.jpg") repeat fixed 0% 0% transparent;}
  
  #content .pageContent
  {background: rgba(255,255,255,.8);}
  
  /* Fond bas de page */
  .footer
  {background: url("http://www.pixenli.com/images/1426/1426869816045963900.jpg") repeat fixed 0% 0% transparent;}
  
  /* Barres Acceuil > Forum */
  .breadBoxTop
  {display: none;}
  
  /* Padding titre */
  .titleBar h1
  {margin-top: 0px;
  padding-top: 15px;}
  
  /* Barre du bas */
  .breadBoxBottom
  {background: rgba(0,0,0,.5);}
  
  /* Contenu index */
  .mainContent
  {background: rgba(240,240,240,.8);}
  
  /* Petites barres de catégories, entre autres */
  .nodeList .categoryStrip
  {background: rgba(0,0,0,.6);
  border-radius: 0px;}
  
  .discussionList .sectionHeaders
  {background: rgba(0,0,0,.6);}
  
  /* Séparation topics */
  .discussionListItem
  {border-bottom: 1px solid rgba(0,0,0,.1);}
  
  /* Séparation parties */
  .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo
  {border-bottom: 1px solid rgba(0,0,0,.1);}
  
  /* Flèche */
  .breadcrumb .jumpMenuTrigger /*Flèche vers le bas sur le bout des barres*/
  {background: transparent url(https://dl.dropboxusercontent.com/u/47007147/FBS2/circle-arrow-down.png) no-repeat;width: 23px;height: 15px}
  
  /* Réponses - Affichages */
  .discussionListItem .stats dl
  {padding: 0px 10px;
  border-left: 1px solid rgba(0,0,0,.1);
  border-right: 1px solid rgba(0,0,0,.1);}
  
  /* Font */
  .message .messageText, .message .signature
  {font-family: Calibri,Verdana,Georgia,"Times New Roman",Times,serif}
  
  .messageText
  {font-family: Calibri,Verdana,Georgia,"Times New Roman",Times,serif}
  
  /* Page select */
  .PageNav a
  {color: #A2A2A2;
    background-color: #F0F0F0;
    border-color: #CECECE;}
  
  /* Barres random */
  .subHeading
  {background: rgba(0,0,0,.5)}
  
  .textWithCount.subHeading .text
  {color: white}
  
  /* Preview topic */
  .xenPreviewTooltip
  {background: white}
  
  .breadcrumb .crust .arrow span /*Flèche vers le côté dans les barres*/
  {background-image: url(https://dl.dropboxusercontent.com/u/47007147/FBS2/breadcrumb-arrow.png);width: 7px}
}
