css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("arenabg.com"), domain("arenabg.ch") {

  body {
    background: url('https://www.toptal.com/designers/subtlepatterns/patterns/zwartevilt.png') !important;    
     margin-top: 0 !important;
     color: #aaa !important;
 }
  
    .p10 {
      padding-bottom: 4px !important;
  }
    
    a, .menu > div > a .fa, td a {
        color: #aaa !important;
    }
    
    a, .menu > div > a .fa:hover {
        color: #eee !important;
    }
    
    
 .fb-like, .fb_iframe_widget, iframe, footer, .arena_10, .arena_9, .header, a[href="/en/torrents/"] > img, .arena_12 {
      display: none !important;
   }
    
    .btn-dark2, .panel-arena .panel-heading, tr, td, #torrents, th, .panel-footer, .p10 {
        border: 1px solid #222 !important;
        background: url('https://www.toptal.com/designers/subtlepatterns/patterns/zwartevilt.png') !important;    
        color: #aaa !important;      
    }
      
   .btn-dark2:hover, .btn:hover, .panel-arena a:hover, .panel-heading a:hover, .btn-dark2.btn-gradient:hover {
       color: #eee !important;      
       text-shadow: 0 0 1px #eee !important;
       text-decoration: none !important;
    }
       
    
    input:hover, inpute:active, input[type="text"]:focus, input[type="text"]:focus, input[type="password"]:focus, input[type="file"]:focus, input[type="email"]:focus, textarea:focus, select:focus {
        background-color: #222 !important;
        box-shadow: none !important;
        color: #aaa !important;
    }
    
    #search-button, .btn-sm {
        background: #222 !important;
        border: 1px solid #111 !important;
        color: #aaa !important;
    }
    
   .panel-arena {
        border: 1px solid #222 !important;
   }
    
    .panel-arena .panel-body {
        background: url('https://www.toptal.com/designers/subtlepatterns/patterns/zwartevilt.png') !important;    
    }
}
