@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("softarchive.net") {
  
  .item_preview_text > .center img {
    
    max-width: 180px;
    max-height: 150px;
    float:left; 
    margin:5px;
    
  }
  
  body 
  {
    background: #222;
  }
  
  .section_main 
  {
    border:0px;
    background:#222;
  }
  
  ul.breadcrumb 
  {
    display: none;
  }
  .item_preview_text > .center
  {
    
    font-size:11px !important;
  }
.item_preview_text {
  font-size: 12px;
  
}
.list_item_title , .list_item_title .item_caption
  {
    font-size: 13px;
    font-family: arial,sans-serif;
    color:#FFC600;
  }
 
  a.category_title 
  {
    display: none !important;;
  }
  .list_item_title, .item_title {
    
    font-weight: bold;
    font-size: 11px !important;
    line-height: 14px;
    
}
  ul.sub_menu 
  {
    background: #181818;
  }
  .sub_menu > li
  {
    background: none !important;
    border: 0px !important;
    
  }
  a.item_caption 
  {
    text-decoration: none;
    text-align: center;
    margin:0px auto;
    color:#000;
  }
  .main_items_list.clear footer
  {
    display: none;
  }
  .tagcloud
  { display: none; }
  
  .main_menu 
  {
    background: #121212 !important;
    border-bottom: 3px solid #A47C2D !important;
    border-right: 0px;
    border-left: 0px;
  }
  .main_items_list li {
  
  list-style: outside none none;
  margin-bottom: 43px;
  height: 210px;
  padding: 10px;
      width: 430px;
    float: left;
    margin: 5px;
    overflow: auto;
     background: #141414 ;
    color: #000 !important;
    border-radius: 5px;
    border-bottom: 1px solid #FFBD00;
}

  
 
  .continue_reading {
  display: none;
}
  .hostings_list {
  display: none;
}
  
  
 .full_topic
  {
    color:#fff !important;
    font-family: arial,sans-serif;
    font-size:13px;
  }
  
 .same_level > ul.pagination {
    text-align: center;
    position: fixed;
   
    background: rgba(21, 21, 21, 0.48) !important ;
    width: 100%;
    left: 0px;
   bottom:0px;
   padding:10px;
   border-bottom:1px solid #A47C2D;
}
  .main_menu li a 
  {
    color:#fff;
  }
  
  .main_menu li  a::before
    {
      background: none !important;
    }
  ul.pagination li a
  {
 background:#FFA200 !important;;
    color:#000;
    border:1ps solid #000 !important;
  }
