css:
@namespace url(http://www.w3.org/1999/xhtml);
 
@-moz-document domain("games.espn.go.com")
{
    
.bracket.locked .slot .actual.selectedToAdvance.winner .abbrev, .bracket.locked .slot .actual.selectedToAdvance.winner .name, .bracket.locked .slot .picked.selectedToAdvance.correct .abbrev, .bracket.locked .slot .picked.selectedToAdvance.correct .name {
    color: #fff;
    background-color: #090;
    padding: 3px;
}

.bracket.locked .slot .actual.selectedToAdvance.loser .abbrev, .bracket.locked .slot .actual.selectedToAdvance.loser .name, .bracket.locked .slot .picked.selectedToAdvance.incorrect .abbrev, .bracket.locked .slot .picked.selectedToAdvance.incorrect .name {
    color:#fff;
    background-color: #C00;
    padding: 3px;
}
    
}
