css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("docs.rainmeter.net") {


	
a{
	color:#c0c0c0;
	text-decoration:none;
}

a:hover{
	color:#f0f0f0;
	text-decoration:underline;
}

p small{
	color:#999;
	font-size:11px;
}


h1 small,h2 small,h3 small{
	color:#999;
}

h1{
	font-size:25px;
	line-height:30px;
	margin-bottom:10px;
}

hr{
	border:0;
	border-bottom:1px solid #202020;
	border-top:1px solid #202020;
	margin:18px 0;
}

img{

	border:1px solid #505050;
}


code,pre{
	color:#c0c0c0;
}

code{
	background-color:#202020;
	border:1px solid #505050;

}

pre{
	background-color:#202020;
	border:1px solid #505050;

}

.highlight a{
	background-color:#202020;
	border:1px solid #505050;
	font-weight:bold;

}


pre code{
	background-color:#202020;

}

dt{
	background-color:#202020;
	border:1px solid #505050;
}

dt:target{
	background-color:#202020;
}

dt code{
	background-color:transparent;
	border:0;
	font-weight:bold;
	padding:0 2px;
}

dd{
	background-color:transparent;
	border:1px solid #505050;

}

table{
	color:#808080;

}

.table td{
	border-top:1px solid #505050;
}

.table-bordered{
	border:1px solid #505050;

}

.table-bordered td,.table-bordered th{
	border-left:1px solid #505050;
}

.table-striped tbody tr:nth-child(odd) td,.table-striped tbody tr:nth-child(odd) th{
	background-color:#181818;
}

body{
	background:#050505 ;

}

#logo{
	background-image:url(data:image/png;base64,);
	background-color:#303030;
	float:left;

}

.container{
	color:#808080;

}

#side-content{
	background:#202020;
}

#category,#search-form{
	background-color:#202020;
	border-bottom:1px solid #505050;
}

#category a{
	color:#808080;
}

#search-form:before{
    background-repeat:no-repeat;
	background:transparent;
	content:" ";
	height:12px;
	margin:10px 0 0 10px;
	pointer-events:none;
	position:absolute;
	width:11px;
	z-index:10;
}

#search{
	background:transparent;
	border:0;
	height:18px;
	padding:8px 25px;
	width:210px;
}

#tree h1,#tree h1 a{
	color:#CCC;
	font-size:14px;
	text-shadow:0 1px 0 rgba(0,0,0,0.5);
}

#tree ul{
	background:#202020;
}

#tree li ul{
	background:#202020;
}

#tree li{
	background:#202020;

}

#tree li:last-child{
	background:#202020;
}

#tree li .expander{
	background:#202020;

}

#tree li .expander.open{
	background:#202020;
	
}

#content{
	background-color:#101010;

}


#index-left{
	border-right:1px solid #505050;

}

header{
	background:#202020;
	border-bottom:1px solid #505050;
	box-shadow:0 1px 0 #202020;

}

header p{
	color:#777;

}

header h1:last-child{
	margin-bottom:20px;
}

sup{
	color:#777;
}

ol.footnote li,p.footnote{
	color:#777;
}

table#contents tr td:nth-child(2){
	color:#999;
	font-size:.9em;
	font-weight:normal;
}

.exampleprev{
	background-color:#202020;
	border:1px solid #505050;

}

.exampleprev .thumbnail{
	color:#d5d5d5;
	max-width:200px;
}

pre .comment,pre .diff .header,pre .javadoc,pre .template_comment{
	color:#9f9f9f;
	font-style:italic;
}

pre .css .rule .keyword,pre .javascript .title,pre .keyword,pre .nginx .title,pre .request,pre .status,pre .subst,pre .winutils{
	color:#808080;
	font-weight:bold;
}

pre .hexcolor,pre .number,pre .ruby .constant{
	color:#099;
}

pre .phpdoc,pre .string,pre .tag .value,pre .tex .formula{
	color:#ffa0a0;
}

pre .coffeescript .params,pre .id,pre .scss .preprocessor,pre .title{
	color:#fff;
	font-weight:bold;
}

pre .clojure .title,pre .javascript .title,pre .lisp .title,pre .subst{
	font-weight:normal;
}

pre .class .title,pre .haskell .type,pre .tex .command,pre .vhdl .literal{
	color:#458;
	font-weight:bold;
}

pre .django .tag .keyword,pre .rules .property,pre .tag,pre .tag .title{
	color:#000080;
	font-weight:normal;
}

pre .attribute,pre .lisp .body,pre .variable{
	color:#ff4040;
}

pre .regexp{
	color:#009926;
}

pre .lisp .keyword,pre .prompt,pre .ruby .symbol .string,pre .symbol,pre .tex .special{
	color:#992273;
}

pre .built_in,pre .clojure .built_in,pre .lisp .title{
	color:#ffffff;
}

pre .cdata,pre .doctype,pre .pi,pre .pragma,pre .preprocessor,pre .shebang{
	color:#f02020;
	font-weight:bold;
}

pre .deletion{
	background:#ffff00;
}

pre .addition{
	background:#ffff00;
}

pre .diff .change{
	background:#ff2222;
}

pre .chunk{
	color:#3333ff;
}

#lightbox-wrap>img{
	border:8px solid #202020;
	box-shadow:0 3px 3px rgba(0,0,0,0.5);
}
 
#top {
    background:#303030;
    border-bottom: 1px solid #505050;
}
 

	
}
