css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain('playstationhax.it'), url-prefix('http://playstationhax.it') {

#header {
	padding: 0.2em 1.5em;
        
}

#custom-header {
	background-image:url(http://i.imgur.com/V3Fgol5.png);
}
#header-image {
background-image:url(http://i.imgur.com/V3Fgol5.png);
}
#mainnav-menu a {
	display: block;
	padding: 1.4em;
	text-decoration: none;
	font-size: 0.85em;
	font-weight: bold;
	font-family: 'Lato', Tahoma, Verdana, Arial;
}

.page-title, .post-title {
	background: #222;
}

.post-title a:link, .post-title a:visited {
	color: #fff;
	text-decoration: none;
}

.post-title a:hover, .post-title a:active{
	color: #ffa;
}
.widgettitle {
	font-family: 'Lato', Tahoma, Verdana, Arial;
	font-weight: bold;
	color: #252525;
	background: #ff7;
	border: 1px solid #e2e2e2;
	padding: 0.5em 0.8em;
	margin-bottom: 1em;
	line-height: 1.4em;
	-webkit-border-radius: 0.4em;
	-moz-border-radius: 0.4em;
	border-radius: 0.4em;
}

}
