css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dragonprime.net") {
.logobg {
  
  background-image: url("http://i.imgur.com/UQDiQ0z.png");
  background-size: 100% 100%;
  height: 115px;
  width: 100%;
  text-align: center;
}
  
.logobg a {
  color: #ffffff;
  box-shadow: 0 0 10px #a19fe4;
  border-radius: 5px;
  font-size: medium;
  background-color: rgba(255,255,255,0.3);
  margin: 5px 5px 5px 5px;
  padding: 5px;
}

.logobg a:hover{
  box-shadow: 0 0 10px #4440ca;
  color: #000000;
  text-decoration: none;
  transition-duration: 2s;
}
  
.user-text {
  position: absolute;
  top: 0px;
}
  
.user-text li{
  font-size: 12px;
}
  
.user-text a{
  font-size: 12px;
  padding: 0px;
  margin: 0px;
  background-color: transparent;
  box-shadow: 0 0 0px #000000;
}
  
.user-text a:hover {
  color: #a19fe4;
  transition-duration: 0.5s;
  box-shadow: 0 0 0px #ffffff;
  
}
  
.user-info {
    overflow: hidden;
  font-size: small;
}
  
.user-avatar a{
  border: 0px solid #000000;
  background-color: transparent;
  box-shadow: 0 0 0px #ffffff;
  border-radius: 0px;
}
  
.user-avatar a:hover {
  box-shadow: 0 0 0px #ffffff;
}
  
.avatar {
  border-radius: 50%;
  width: 85px;
  height: 85px;
    -webkit-transition-duration: 1s;
    -moz-transition-duration: 1s;
    -o-transition-duration: 1s;
    transition-duration: 1s;
     
    -webkit-transition-property: -webkit-transform;
    -moz-transition-property: -moz-transform;
    -o-transition-property: -o-transform;
    transition-property: transform;
     
    overflow:hidden;
  padding: 5px;
  margin: 20px 0px 20px 0px;
}
  
.avatar:hover {
    -webkit-transform:rotate(360deg);
    -moz-transform:rotate(360deg);
    -o-transform:rotate(360deg);
  overflow: hidden;
  box-shadow: 0 10 10px #000000;
  }

@-moz-keyframes rotate {
    from {-moz-transform: rotate(0deg);}
    to {-moz-transform: rotate(360deg);}
}
  
.blockheadright {
  background: url("http://i.imgur.com/uhsKjUi.png") no-repeat scroll right top #006BAD;
  background-size: 100% 100%;
  width: auto;
  height: auto;
  padding: 0px;
  text-align: center;
  color: #fff;
  font-size: 11px;
  padding: 3px;
}
  
.blockhead {
  background: url("http://i.imgur.com/uhsKjUi.png") no-repeat scroll right top #006BAD;
  background-size: 100% 100%;
  width: auto;
  height: auto;
  padding: 0px;
  text-align: center;
  color: #fff;
  font-size: 11px;
  padding: 3px;
  border-radius: 3px;
}
  
.blockheadleft {
  background: url("http://i.imgur.com/ls1A1C1.png") no-repeat scroll right top #006BAD;
  background-size: 100% 100%;
  width: auto;
  height: auto;
  padding: 0px;
  text-align: center;
  color: #fff;
  font-size: 11px;
  padding: 3px;
}
  
.tabmenu {
height: 23px;
width: 100%;
  margin: 0px;
  padding: 0px;
  background-image: none;
  z-index: 99;
}
  

.tabmenu td{
  background-image: url("http://www.squier-talk.com/forum/styles/bluecarbon/gradients/gradient_tcat.gif");
  margin: 0px;
  padding: 0px;
  border: 1px solid transparent;
}
  
.tabmenu td:hover{
  background-image: url("http://www.squier-talk.com/forum/styles/bluecarbon/gradients/gradient_tcat.gif");
  background-color:rgba(255, 255, 255, 0.02);
  transition-duration: 1s;
  color: #ffffff !important;
  border: 1px solid rgba(255, 255, 255, 0.02);
  overflow: auto;
}
  
.tabmenu a{
  color: #98d2ec !important;
  padding: 2px;
}
  
hr {
	margin: 10px 0 10px 0 !important;
	border: 0 !important;
    height: 1px !important;
	width: 100% !important;
    background: #333 !important;
    background: -webkit-gradient(linear, left top, right top, color-stop(0%,hsla(274,100%,43%,0)), color-stop(50%,hsla(274,100%,43%,.75)), color-stop(100%,hsla(274,100%,43%,0))) !important;
    background: -webkit-linear-gradient(left, hsla(217,100%,43%,0) 0%, hsla(217,100%,43%,1) 50%, hsla(217,100%,43%,0) 100%) !important;
    background:    -moz-linear-gradient(left, hsla(217,100%,43%,0) 0%, hsla(217,100%,43%,1) 50%, hsla(217,100%,43%,0) 100%) !important;
    background:     -ms-linear-gradient(left, hsla(217,100%,43%,0) 0%, hsla(217,100%,43%,1) 50%, hsla(217,100%,43%,0) 100%) !important;
    background:      -o-linear-gradient(left, hsla(217,100%,43%,0) 0%, hsla(217,100%,43%,1) 50%, hsla(217,100%,43%,0) 100%) !important;
    background:         linear-gradient(left, hsla(217,100%,43%,0) 0%, hsla(217,100%,43%,1) 50%, hsla(217,100%,43%,0) 100%) !important;
	}
  
.tabmenu a:hover{
color: #ffffff !important;  
  text-decoration: none;
  cursor: pointer;
}
  
.tabmenu a:link {
  color: #ffffff;
}
  
.tabmenu img{
  display: none;
}
  
.tabmenuitem:hover {
  box-shadow: 0 0 20px #98d2ec;
  transition-duration: 1s;
  color: #ffffff !important;
 }
  
.mirrortab_first {
  background-image: none;
}  
  
  .mirrortab_back {
    border-radius: 5px;
    border-top: 0px;
}
  
  .mirrortab_last {
  background-image: none;
}
  
  .navPages {
    border: 1px solid #a19fe4;
    border-radius: 10%;
  }
  
  .smalltext {
    text-align: center;
  }
  
  input[type="text"] {
    border-radius: 5px;
}
  
img[src*="dragon-prime.jpg"] {
  display: none; 
}
}
