css:
@-moz-document domain("tweetdeck.twitter.com") {
/* fonts & white bg colors */

body, #container {
    font-family: 'b yekan', Tahoma; 
    text-shadow: none;
}
textarea, p{
    font-family: 'b yekan', Tahoma; 
    font-size:16px;

}
.column{width:33%!important}   
.tweet-body, .margin-v--10, .margin-vl {
    direction:rtl; 
    text-align:right;
}
.btn-with-icon-small {
    padding: 4px 10px !important;
}
.menu-button{
    padding-top:3px !important;    
}
.prf .location {
    text-align: center;
}
.app-search-input {
    box-shadow: none !important;
}
}
