css:
@-moz-document url-prefix('https://play.google.com/'), url-prefix('http://play.google.com/') {
	.action-bar {
		background: rgb(82, 82, 82);
	}
	.action-bar-dropdown-top {
		color: white;
	}
	.action-bar-link a, .action-bar-link a:visited {
		color: white;
	}
	.action-bar-title {
		color: rgba(244, 244, 244, 0.95);
	}
	.app-compatibility-final, .app-compatibility-initial {
		color: #BDBDBD;
	}
	.buy-button-container.play-button .price {
		background: #b3c833;
	}
	.card .price {
		background: #EEEEEE;
	}
	.card .reason-set {
		background: #EEEEEE;
	}
	.card-content {
		background: #EEEEEE;
	}
	.card.apps .cover, .card.devices .cover, .card.people .cover {
		background-color: #EEEEEE;
	}
	.card.medium-plus .reason {
		margin-left: 10px;
	}
	.card.small .reason:first-child {
		margin-left: 10px;
	}
	.cluster-heading {
		color: #FFF;
		font-family: Verdana, Geneva, sans-serif;
		font-weight: 100;
		text-decoration: none;
		text-shadow: 2px 2px #424242;
	}
	.cluster-heading a {
		color: #FFF;
	}
	.details-actions .play-button-wrapper, .details-actions .play-button {
		background-color: #b3c833;
		margin-right: 10px;
	}
	.details-info {
		background-color: #616161;
		/*background-color: #424242;*/
	}
	.details-section {
		background-color: #9E9E9E;
	}
	.details-section.description.simple .text-body {
		color: whitesmoke;
	}
	.details-section.screenshots .thumbnails {
		height: 460px;
	}
	.details-section.screenshots .thumbnails .screenshot {
		height: 400px;
	}
	.details-section.screenshots .thumbnails-wrapper {
		overflow-y: visible;
	}
	.displayed-child {
		color: #616161;
	}
	.expand-pages-container:nth-child(0) {
		height: 460px;
	}
	.header-star-badge {
		color: whitesmoke;
	}
	.heading {
		color: #FFF;
		font-family: Verdana, Geneva, sans-serif;
		font-weight: 100;
		text-decoration: none;
		text-shadow: 2px 2px #424242;
	}
	.heading a {
		color: #FFF;
	}
	.inapp-msg {
		color: turquoise;
	}
	.info-container .document-subtitle {
		color: #BDBDBD;
	}
	.info-container .document-title {
		color: #FFF;
	}
	.meta-info .content, .meta-info .category, .meta-info .dev-link {
		color: white;
	}
	.nav .apps .chosen, .nav .apps .chosen .sub-nav-link {
		color: #8d9d29;
		font-weight: 500;
	}
	.nav .shop-link, .nav .library-link, .nav .wishlist-link {
		color: white;
	}
	.no-hover-zone .nav .non-store-sub-nav {
		background: rgba(85, 85, 85, 0.74);
	}
	.paragraph-end {
		background: none;
		background-image: none;
	}
	.play-button {
		background-color: #737373;
		border: 1px solid rgba(245, 245, 245,0.37);
		color: #fff;
	}
	.play-button.apps {
		background-color: #b3c833;
	}
	.quoted-review .review-text {
		color: whitesmoke;
	}
	.rating-box {
		background: #EEEEEE;
	}
	.review-body {
		color: whitesmoke;
	}
	.review-title {
		color: white;
	}
	.screenshots .details-trailer, .details-trailer .video-image {
		height: 400px;
		width: 600px;
	}
	.secondary-sub-nav-option .sub-nav-link {
		color: rgba(255, 255, 255, 0.81);
	}
	.show-more-end {
		background: linear-gradient(to bottom,rgba(97, 97, 97,0),rgba(97, 97, 97, 1));
	}
	.sub-nav-divider {
		background: white;
	}
	.sub-nav-quick-link {
		background: rgba(100, 100, 100, 1);
	}
	.whatsnew .recent-change {
		color: whitesmoke;
	}
	body {
		background-color: #9E9E9E;
	}
	.paragraph-end.details-light {
		background: none;
	}
	.details-section .heading {
		color: #FFF;
	}
	.cluster-heading .subtitle {
    	    	color: #FFF;
	}
	.see-more.play-button.small.id-track-click.apps.id-responsive-see-more {
    	    	text-shadow: none ;
	}
}
