css:
@-moz-document domain("www.pralek.cz") {

  
@media all and (max-width: 30em) { 
  
body {

  background-color: #CBAEA8;

  font-family: Helvetica,sans-serif;

}
  
input, select {text-align: center}

h1 .red { color: #68483B;}
 
li, dd, dt {
    line-height: 180%;
    margin-top: 0.3em;
}

h1{font-size:1.7em;}

h2      { color:#68483B; float:none; margin: 0.7em; font-size:1.2em;}

h2 + p  { clear:both;}

 p  { line-height:180%}

a       { color: #68483B;}

ul      {margin: 0;padding: 0.4em;}

ul li   {margin: 0;padding: 0.4em;}

.socialIcons {text-align:center; margin: 2em;}}

@media all and (min-width: 64em) and (max-width:225em) {

body {

  background-color: #CBAEA8;

  font-family: Helvetica,sans-serif;

}
  
  li, dd, dt {
    line-height: 180%;
    margin-top: 0.3em;
   
}
  
  textarea, p {
    line-height: 180%;
}



h1 .red { color: #68483B;}

h2      { color:  #68483B; float:none; margin: 2em;}

h2 + p  { clear:both;}

a       {text-align:center; color: #68483B;}

ul li   {margin: 1em;padding: 0.3em;}

.socialIcons {text-align:center; margin: 3em;}}


@media all and (min-width: 48em) and (max-width: 64em) {
  
body {

  background-color: #CBAEA8;

  font-family: Helvetica,sans-serif;

}
  li, dd, dt {
    line-height: 180%;
    margin-top: 0.6em;
}
  textarea, p {
    line-height: 130%;
 
}
  h1 .red { color: #68483B;}

h1{font-size:2em;}

h2      { color: #68483B; float:none; margin: 1.3em; font-size:1.6em;}
  
 p  { line-height:180%}

h2 + p  { clear:both;}

a       { color: #68483B;}

ul      {margin: 0.3em;padding: 0.3em;}

ul li   {margin: 0em;padding: 1em;display:block; max-width: 14em;float: left;}

.socialIcons {text-align:center; margin: 1em;}

ul + li{ clear:both;}

}
}
