css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wiki.blender.org") {
  
body, p, ul, ol, div {
    color: #DBB49C;
    font-size: 16px;
}
  
#col-content {
    background: none repeat scroll 0% 0% #2D2424;
}  
  
#column-two {
    background: none no-repeat scroll 0% 0% #333;
    border-left: 1px solid #211818;
}
  
#column-one {
    background: none no-repeat scroll 0% 0% #333;
    border-right: 1px solid #211818;
}
    
#navtree_selector {
    background: none repeat scroll 0% 0% #261A1A;
}
    
#navtree_selector .dd_button {
    background: #483A3A;
    border: 1px solid #534C4C;
}
    
.grid_menu_2 .grid_item {
    background-color: #120F0F;
    border: 1px solid #3F3030;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.3);
}    
    
#col-content a {
    color: #3FA7C3;
}    
    
.grid_item h3.bf {
    background: no-repeat scroll right center rgba(0, 0, 0, 0.92);
}    
    
.grid_menu_3 .grid_item {
    background-color: #010101;
    border: 1px solid #423232;
}
    
.grid_item h3.bi {
    background: none no-repeat scroll right center transparent;
}    
    
.dtree a {
    color: #F29494;
}
    
a {
    color: #87BFF6;
}    
    
.note_title {
    border: 1px solid #150F0F;
    background: none repeat scroll 0% 0% #4D3333;
}    
    
div.refbox {
    background-color: #3C2525;
    border: 1px solid #E1956C;
}    

.literal {
    color: #D5794F;
}    
    
.NiceTip {
    border: 1px solid #741818;
    background: none repeat scroll 0% 0% #861D1D;
    color: #DECCCC;
}
  
#headerWrapper {
    background: -moz-linear-gradient(center top , #9AA4AE, rgba(118, 137, 156, 0.53)) repeat scroll 0% 0% transparent;
}
    
#footer {
    background: -moz-linear-gradient(center top , #95A0AC, rgba(118, 137, 156, 0.38)) repeat scroll 0% 0% transparent;
}
    
.tableNormal {
    background-color: #3F3434;
    border: 1px solid #5F4337;
}
    
   
}
