css:
@namespace url(http://www.w3.org/1999/xhtml);
 @-moz-document domain("mangatraders.org") {
    /* SET DOWNLOAD LINK FOR FONT */
    body {
        font-family:'haxrcorp s8'!important;
        font-size: 12px!important;
    }
    /* Search Bar */
    .typeahead {
        margin-left: 20px!important;
        height: 28px !important;
        width: 700px!important;
        overflow-y: hidden!important;
        margin-top: -3px!important;
    }
    /* Search Bar Drop Down */
    .tt-dropdown-menu {
        width: 730px!important;
        overflow-y: hidden!important;
    }
    /* Nav Bar Logo */
    a.navbar-brand {
        margin-top: -5px!important;
        min-height: 40px!important;
        height: 40px!important;
    }
    /* Username Holder */
    a.dropdown-toggle {
        min-height: 40px!important;
        height: 40px!important;
    }
    /* Username */
    .nav.navbar-nav.navbar-right {
        margin-right: -18px!important;
        margin-top: -6px !important;
    }
    .navbar {
        min-height: 1px !important;
        height: 37px !important;
        border-bottom: none!important;
        box-shadow: none !important;
    }
    .container {
        
    }
    /* Profile Image 'div.IMG' Fix */
    img.myProfileImage {
        padding-top: 0px!important;
    }
    /* Comments Image 'div.IMG' Fix */
    .comments_Image,.commentForm_image {
        padding-top: 0px!important;
    }
    /* 'Read Online' Image */
    img {
        margin-top: -18px!important;
        max-width: 780px!important;
        height: auto!important;
        border: none!important;
    }
     /*Main Page Menu Bar */
    ol.breadcrumb {
        padding-left: 17%!important;
        box-shadow:  2px 5px 2px 1px #888 !important;
        color: #6BAADF !important;
        margin-top: -18px !important;
    }
    div.well,.list {
        box-shadow:  2px 5px 2px 1px #888 !important;
    }
    /* Header Break Line */
    hr {
        visibility: hidden !important;
    }
    /*Manga Directory Volume Download Icon */
    .glyphicon.glyphicon-check,.glyphicon.glyphicon-cloud-download {
        text-indent: 100%!important;
        white-space: nowrap!important;
        overflow: hidden!important;
        background-repeat: no-repeat!important;
        background:url("http://puu.sh/gNwWD/3718a689d3.png")!important;
        height: 14px !important;
    }
    fault.btn-xs.hideChap, .btn.btn-default.btn-xs.hideChap:hover {
        margin-top: -63px !important;
    }
    /* List by Volume/Chapter Button */
    a.btn.btn-primary,a.btn.btn-info {
        background: none!important;
        border: none !important;
        border-bottom: 1px solid #000 !important;
        font-weight: bold!important;
    }
    b {
        font-weight: normal !important;
    }
     
     
     /* COLORS */
     
     /* Username Color */
     a.dropdown-toggle {
         color: #cccaca!important;
     }
     /* Link Colors */
     a {
         color: #61646B!important;
     }
     
     
     
    /* DISABLED */
     
    /* Latest Chapters Next/Previous Buttons (Hides suscribe button, fix?) */
    button.btn.btn-default {
        display: none!important;
    }
    select#changePageSelect {
        display: none!important;
    }
    /* 'Read Online' Nav */
    .breadcrumb_nav {
        display: none!important;
    }
    /* 'Read Online' Footer */
    .containerBottom {
        display: none!important;
    }
    /* 'Read Online' Download Ch/Vol */
    .btn.btn-xs.btn-danger, .btn.btn-xs.btn-primary {
        display: none !important;
    }
    /* 'Read Online' Page/Chapter */
    #changeChapterSelect {
        display: none!important;
    }
    /* Footer */
    footer, p.footer {
        display: none!important;
    }
    /* Top Manga New Search */
    a.btn.btn-xs.btn-warning {
        display: none!important;
    }
    /* ALL Icons */
    .glyphicon.glyphicon-chevron-right, .glyphicon.glyphicon-chevron-left, .glyphicon.glyphicon-user, .glyphicon.glyphicon-cog, .glyphicon.glyphicon-star-empty, .glyphicon.glyphicon-time, .glyphicon.glyphicon-remove, .glyphicon.glyphicon-ok-circle, .glyphicon.glyphicon-edit, .glyphicon.glyphicon-fire.margRight10, .glyphicon.glyphicon-search.margRight10, .glyphicon.glyphicon-ok.subscribed.subscribeIcon, .glyphicon.glyphicon-chevron-up, .glyphicon.glyphicon-star, .glyphicon.glyphicon-font,.glyphicon.glyphicon-th-large,.glyphicon.glyphicon-th,.glyphicon.glyphicon-plus-sign.subscribeIcon,.glyphicon.glyphicon-camera,.glyphicon.glyphicon-lock,
     .glyphicon.glyphicon-eye-open,.glyphicon.glyphicon-eye-close,.glyphicon.glyphicon-file,.glyphicon.glyphicon-book,.glyphicon.glyphicon-plus-sign{
        display: none!important;
    }
}
