css:
@-moz-document domain("mkboards.com") {
/****************
Elements
****************/

body {
background-image: url('http://cdn5.psdvault.com/img/2013/06/1-bg-texture.jpg');
}

/* Links */
.ugc a:hover, .ugc a:focus {
  background:none;
  box-shadow:none;
  color:#2079B8;
}
.ugc a:hover {
    color: rgb(17, 176, 49);
    box-shadow:none !important;
}
.ugc a:focus {
  color:none;
}

/****************
Global
****************/

.navTabs .navTab.selected .tabLinks {
  background-color: #002249;
}
.navTabs .navTab.selected .navLink {
  color: #FFFFFF;
  background-color: #002249;
}

.forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
  color: #CCC;
  background-color: #222;
}
.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
  color: #CCC;
  background-color: #111;
  border-bottom: 1px solid #000000;
}
.sectionMain {
  background-color: #111;
}
  
.node .nodeLastPost {
  color: #ccc;
  background-color: #002249;
  box-shadow: none;
  /* text-shadow: rgba(0,0,0,0.5) 1px 1px 1px,rgba(0,0,0,0.5) -1px -1px 1px,rgba(0,0,0,0.5) 1px 0 1px,rgba(0,0,0,0.5) 0 1px 1px; */
    /*text-shadow: rgba(0,0,0,0.5) 1px 1px 0,rgba(0,0,0,0.5) -1px -1px 0,rgba(0,0,0,0.5) 1px 0 0,rgba(0,0,0,0.5) 0 1px 0,rgba(0,0,0,0.7) 2px 2px 2px;*/
}
  		.node .nodeLastPost a {
  			color:#BDE4FF;
      }
  
.nodeList .categoryStrip {
  background-color: #002249;
}
.nodeList .categoryStrip .nodeTitle a {
  color: #BDE4FF;
  text-shadow: none;
}

.sidebar {
  background:#000;
  border: 1px solid #666;
}
	.tabs li a, .tabs.noLinks li {
 		background: #65A5D1;
    }
	.tabs li.active a, .tabs.noLinks li.active, .profilePage .tabs.mainTabs li.active a {
  		color:#BDE4FF;
  		background-color: #002249;
    }

/* Site headers and footers */
.breadcrumb {
  background-color: #002249;
}
        
/* Announcements */
.PanelScroller .panel, .PanelScrollerOff .panel {
  background-color: #002249;
}
.PanelScroller .scrollContainer { background:#000; }
#Notices .panel .noticeContent { color:#BDE4FF;}


/****************
Thread list
****************/
.discussionListItem {
  background-color: #111;
  border-bottom: 1px solid #000000;
}
.discussionListItem .posterAvatar, .discussionListItem .stats {
  background-color: #111;
}
.discussionListItem .stats dl {
  border: none;
}


/****************
Thread Pages
****************/

/* Title and top bar information */
a.PageNavPrev, a.PageNavNext {
  color:#fff;
}
.PageNav a {
  color: #BDE4FF;
  background-color:rgba(0,0,0,0.0);
  border: 1px solid #BDE4FF;
}
#pageDescription {
  color: #ccc;
}


#exposeMask {
  background:#000 !important;
}

/* Main forum post content */
.messageList .message {
  background-color:#111;
  border-top: 2px solid #222;
  border-bottom: #000 2px solid;
  border-right: #002249 solid 2px;
  border-left: #002249 solid 2px;
}
.messageList {
  border-bottom: #002249 solid 2px;
}
.primaryContent {
  color: #CCC;
  background-color: #111;
  padding: 10px;
  border: none;
}
.sidebar .section .secondaryContent h3 {
	  border-bottom: 1px solid #444;
}
.secondaryContent {
  color: #aaa; /* "X likes this" */
  box-shadow:rgba(0,0,0,1) 3px 3px 5px inset;
  background-color: #0a0a0a;
  border-bottom: #002249 solid 2px;
}
  .Menu .primaryContent {
  background-color: #111;
  }
  .Menu .secondaryContent {
  background-color: #080808;
  }
  .Menu .sectionFooter {
  background-color:#002249;
  }

  .subForumsGrid .blockLinksGrid {
  border-bottom: 1px solid #333;
  }
#content .pageContent {
  box-shadow: rgba(0,0,0,1) 5px 5px 30px;
  background:#000;
}
.message .messageInfo {
  color: #bbb; /* text color of posts */
  text-shadow:rgba(0,0,0,1.0) 1px 1px 2px;
}
.message .messageMeta {
  clear: both; /* This Clear makes it so the "Last edited" thing doesn't appear on the same line as all the little option buttons. */
}


/* Viewer list stuff */
.threadViewers, .discussionList .sectionHeaders, .sectionFooter, .subHeading {
  border-radius: 5px;
  background-color: #002249;
  color: #BDE4FF;
}
  	.discussionList .sectionHeaders dd a[href]:hover {
    	background-color: rgb(101, 165, 209);
	}



/* User profile stuff on the left */
.messageUserBlock div.avatarHolder, .messageUserBlock h3.userText,.messageUserBlock .extraUserInfo,.messageUserBlock {
  background-color: #222;
}
.messageUserBlock {
  box-shadow:rgba(0,0,0,0.5) 2px 2px 3px;
  border-top: solid #444 1px;
  border-left: solid #444 1px;
  border-bottom: solid #000 1px;
  border-right: solid #000 1px;
}
/* .avatar img, */ .avatarCropper {
  padding:none;
  border: 1px solid #000000;
  box-shadow: #000 0 0 7px;
  background:rgba(0,0,0,0.0);
}
.avatar img {
  padding:0;
  color:rgba(0,0,0,0.0);
}
.avatar img, .avatar .img, .avatarCropper {
  background-color: rgba(0,0,0,0.0);
}

.username {
  text-align:left;
}
    .messageUserBlock a.username {
    color:#ccc;
    text-shadow: #000 0 0 3px,#000 0 0 2px,#000 0 0 1px;
    }
    .messageUserBlock .arrow {
    position: absolute;
    top: 10px;
    right: -10px;
    display: none;
	}
#content .pageContent {
  color: #ccc;
}
.message .postNumber:hover, .message .messageMeta .control:hover {
  text-shadow: none;
  transition: .1s;
}
.message .messageMeta .control:hover, .PageNav a:hover {
  text-decoration: none;
  color: #fff;
  background-color: #005898;
  border-color: #6cb2e4;
}
.ribbon9 {
  color: #000000;
}
  
.message .likesSummary:hover {
  border: 1px solid rgba(252, 252, 255,0);
  background-color: rgba(252, 252, 255,0);
}  

/* "Last edited" Section */
.message .editDate {
  margin-top: 5px;
  margin-bottom: 5px;
  color: #6D98C8;
  /* border: 1px solid #000000; */
  /* background-color: #002249; */
  background:none;
  border:1px solid #6D98C8;
  text-align:center;
  display:inline-block;
  padding:4px 8px;
  float:right;
}

/* Signature section */
.message .signature {
  border-top: 1px solid #222;
  padding-top:50px;
}

/* Spoiler */
.hasJs .bbCodeSpoilerText {
  background-color: #0a0a0a;
  box-shadow: inset 3px 3px 5px rgba(0,0,0,0.5);
  margin: 0 auto;
  border: 1px solid #000000;
}
    
/* Quotes */
.bbCodeQuote .quoteContainer {
  color:#888;
  background:none;
  box-shadow: inset 3px 3px 5px rgba(0,0,0,0.5),3px 3px 5px rgba(0,0,0,0.5);
}
.bbCodeQuote {
  border: #002249 solid 2px;
  box-shadow: 1px 1px 0 rgba(0,0,0,1);
}
.bbCodeQuote .attribution {
  color: #BDE4FF;
  background: #002249;
  border-bottom: 1px solid #002249;
  text-shadow:1px 1px 0 rgba(0,0,0,1);
}
.bbCodeQuote .quoteContainer .quoteExpand {
  background: linear-gradient(to bottom, rgba(0,0,0,0) 0%, rgba(0, 0, 0,1) 120%);
  text-shadow: none;
}

.button {
    box-shadow: 2px 2px 4px rgb(0, 0, 0);
}

/****************
User profile page
****************/
.profilePage .mast {
  background-color: #111;
}
.profilePage .mast .section.infoBlock {
  background: none;
  border: #444 solid 1px;
}
.textWithCount.subHeading .text {
  color:#BDE4FF;
}
.profilePage .tabs.mainTabs {
  background: none;
  border-left: none;
}
.profilePage .mainProfileColumn {
  background-color: #111;
}
.profilePage .mast, .profilePage .mainProfileColumn {
  border: 1px solid #444;
}
.profilePage .primaryUserBlock .lastActivity {
  border-top: 1px solid #444;
}
.profilePage .mast .section.infoBlock h3 {
  border-bottom: 1px solid #444;
}
.profilePage .mast .followBlocks .primaryContent.avatarHeap {
  border: 1px solid #444;
}

/****************
Conversations
****************/
.conversation_view .messageList.withSidebar {
  background:none;
}
.conversation_view .messageList {
  border: none;
}

/****************
Quick reply
****************/
.redactor_toolbar li.redactor_btn_group ul {
  background-color: #ccc;
  }
.redactor_toolbar {
  background-color: #111;
}
/*.redactor_box .redactor_smilies {
  background-color: #111;
}*/
.xenForm .ctrlUnit {
  background-color: #111;
  border-right: 1px solid #444;
  border-left: 1px solid #444;
}
.xenForm .ctrlUnit.submitUnit {
  border-bottom: 1px solid #444;
}
.xenForm fieldset + .ctrlUnit, .xenForm .formGroup + .ctrlUnit, .xenForm .submitUnit {
  border-top: 1px solid #444;
}

/****************
Search
****************/
.formPopup {
  background-color: #111;
}
#QuickSearch {
  background-color: #111;
}

/****************
Ads
****************/
.mkboards_ads {
    display:none;
}

/****************
Credit
****************/
#copyright:after {
  content: " | Theme created by Leon Williams | Â©2014 TheS2Life. All rights reserved.";
}
}
