css:
@-moz-document domain("wiimmfi.de") {
body {
margin: 0 .4em;
padding: 0;
background-color: #111;
font-family: 'Roboto', Arial, Helvetica, san-serif;
  color: #999;
}

a {
text-decoration: none;
color: rgb(169, 135, 46);
}

h1.narrow, h2.narrow, h3.narrow, h4.narrow, h5.narrow, h6.narrow {
margin-bottom: 10px;
color: #ccc;
}

h1, h2, h3, h4, h5, h6 {
color: #2964FF;
}

.table00, .table01, .table10, .table11 {
border-style: solid;
border-width: 0.15em;
border-color: #aaa;
border-spacing: 1px;
margin: 50px auto;
width: 720px;
text-align: center;
  background-color: #000;
  box-shadow: rgba(0,0,0,1.00) 0 0 50px;
}

.table00 th, .table01 th, .table10 th, .table11 th {
background-color: #000151;
color: #0064B8;
}

.table00 tr:hover, .table01 tr:hover, .table10 tr:hover, .table11 tr:hover {
background-color: #00194F;
}

.table00 tr, .table01 tr, .table10 tr, .table11 tr, .table00 .tr0, .table01 .tr0, .table10 .tr0, .table11 .tr0 {
background-color: #1B1B1B;
color: #aaa;
}

.table00 .tr1, .table01 .tr1, .table10 .tr1, .table11 .tr1 {
background-color: #131313;
}

.table11 .tr2 {
background-color: #523800;
}

.text, .text-warn {
    background-color: #000000;
    box-shadow:none !important;
}

}
