css:
@-moz-document domain(youtube.com) {


  #yReplayBox {
margin: 10px 0px 0px -1px!important;
border-radius: 5px 0px 0px 0px!important;
}

.page-loaded .yt-base-gutter, .page-loaded #page, .page-loaded #appbar-content, body, html {
 background: url(http://i73.servimg.com/u/f73/17/57/29/64/124.png) no-repeat left bottom fixed, url(http://i73.servimg.com/u/f73/17/57/29/64/216.png) no-repeat right bottom fixed, url(http://i184.photobucket.com/albums/x177/crisuskeer/ISML/2012/site/starfield2012.gif) 50% 0% fixed #000;background-color: #000!important;
}


#appbar-guide-menu, .guide-flyout {
-moz-box-sizing: border-box;
-webkit-box-sizing: border-box;
box-sizing: border-box;
background: rgba(0, 0, 0, 0.9)!important;
z-index: 1999999999;
}

.guide-view-more {
color: #FFF!important;
text-align: left;
}

.guide-item {
color: #00FF14!important;
font-size: 12px;
}



.branded-page-v2-primary-column-content, .branded-page-v2-secondary-col .yt-card, .c4-module-is-editable, #watch7-content, #watch7-sidebar{
background-color: rgba(2, 5, 2, 0.6)!important;
   border: 1px solid rgba(0,0,0,0.5);
    color:#fff!important;
   border-radius: 5px !important;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.5), inset 0 1px rgba(255, 255, 255, 0.3), inset 0 1px rgba(255, 255, 255, 0.2), inset 0 0px 20px rgba(255, 255, 255, 0.25), inset 0 -15px 30px rgba(0, 0, 0, 0.3);
  /* -webkit-box-shadow: 0px 0px 5px 1px #000, inset 0 1px rgba(255,255,255,0.3), inset 0 0px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3); */
}
  
  #watch8-action-buttons .yt-uix-button, #watch8-action-buttons .yt-uix-button:hover, #action-panel-dismiss, #action-panel-dismiss:hover {
background: #0AFF00!important;
border: none;
}

  
  /* khung ẩn hiện bên tay trái */
  .guide-pinned .guide-pinning-enabled #appbar-guide-menu, #appbar-nav, body #footer-container, #yt-masthead-container{
  background-color: rgba(2, 5, 2, 0.5)!important;

      }
        .guide-pinned .guide-pinning-enabled #appbar-guide-menu a{
          color: #00FF17!important;
          }

.guide-pinned .guide-pinning-enabled #appbar-guide-menu {
border-right: 1px solid rgba(0, 0, 0, 0.5)!important;
-webkit-box-shadow: none;
-moz-box-shadow: none;
box-shadow: none;
}
            #masthead-appbar {
position: relative;
background-color: rgba(0, 0, 0, 0.7)!important;
border-bottom: 1px solid #E8E8E8;
height: 40px;
line-height: 40px;
}
            
            
  /* khung dưới cover */
  .c4-module-is-editable{margin-top: 10px; }
a.yt-uix-button.yt-uix-button-epic-nav-item, button.yt-uix-button-epic-nav-item, .epic-nav-item{
  border: none;
padding: 0 3px 3px;
cursor: pointer;
background: none;
color: #00FF17!important;
font-size: 12px;
font-weight: normal;
height: 29px;
line-height: 29px;
vertical-align: bottom;
-moz-box-sizing: content-box;
-webkit-box-sizing: content-box;
box-sizing: content-box;
-webkit-border-radius: 0;
-moz-border-radius: 0;
border-radius: 0;
display: inline-block;
}
    a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading {
color: #00FF5A!important;
font-weight: bold;
}



        
    /* xóa nền trắng của khung */      
  .yt-card {
margin: 10px 0;
border: 0;
background: transparent!important;
-webkit-box-shadow: 0 1px 2px rgba(0,0,0,.1);
-moz-box-shadow: 0 1px 2px rgba(0,0,0,.1);
box-shadow: 0 1px 2px rgba(0,0,0,.1);
-moz-box-sizing: border-box;
-webkit-box-sizing: border-box;
box-sizing: border-box;
}

  #watch7-sidebar {
/* position: relative;
margin-top: -409px!important; */
margin-bottom: 20px;
-moz-transition: margin-top .3s ease-in-out,padding-top .3s ease-in-out;
-webkit-transition: margin-top .3s ease-in-out,padding-top .3s ease-in-out;
-webkit-transition: margin-top .3s ease-in-out,padding-top .3s ease-in-out;
-o-transition: margin-top .3s ease-in-out,padding-top .3s ease-in-out;
transition: margin-top .3s ease-in-out,padding-top .3s ease-in-out;
}
  .feed-item-container .feed-item-main, #watch-description, h1.yt, .video-list .video-list-item .description, .video-list .video-list-item .stat {
color: #FFF!important;
}
  .feed-item-container .feed-item-main a, #watch-description a, h1.yt a, .video-list .video-list-item .title {
color: #24FF00!important;
}
  
  .yt-ui-ellipsis {
background-color: transparent!important;
display: block;
line-height: 1.3em;
overflow: hidden;
position: relative;
text-overflow: ellipsis;
white-space: normal;
word-wrap: break-word;
}
  
  
  .yt-lockup {
position: relative;
line-height: 1.3em;
color: #FFF!important;
}

.channel-header .branded-page-header-title .branded-page-header-title-link {
color: #24FF00!important;
font-size: 20px;
font-weight: normal;
text-shadow: none;
}

.pl-video-title-link {
display: inline-block;
line-height: 1.3em;
color: #00FF1F!important;
font-size: 13px;
font-weight: bold;
}

.pl-video-owner a, .pl-video-added-by a {
color: #00FF1F!important;
}

.pl-video-title {
min-width: 284px;
padding: 15px;
overflow: hidden;
font-size: 12px;
color: #00FF1F!important;
}









































}
