css:
@-moz-document url("https://www.di.fm/member/favorite/channels"), url-prefix("https://www.radiotunes.com/member/favorite/channels") {
.channel-list {
	min-height: 1em !important;
}
.column, .channel-item {
	width: auto !important;
}
.content-area.full-width {
    padding: 0 .5em !important;
}
.column {
	float: none !important;
}
.available-channels .channel-item {
    float: left !important;
}
#member-favorite-channels .channel-item {
	padding-top:    2px;
	padding-bottom: 2px;
	margin: .2em;
}
.order-number {
	font-size: 16px !important;
	line-height: 36px !important;
	position: static !important;
	margin-right: 0 !important;
}
#member-favorite-channels .columns .channel-item.sortable-ghost .order-number {
	display: initial;
	visibility: hidden;
}
.icon-grip {
	display: none;
}
}
