css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(gamer.nl) {

* {
/*     font-family: game; */
/*     font-weight: bold !important; */
    box-shadow: none !important;
    border-radius: 0 !important;
}

/* html {
    transform: scale(1.2, 1.2);
    -moz-transform-origin: 50% 0;
} */

body {
        background: grey url("http://arstyle.org/uploads/posts/2010-01/1264346310_1252171602_1.jpg") fixed no-repeat 80% 20% / 175% auto;
}

/*BG None*/
.site-grid, .comments {
    background: none;
}

/*Transparent BG*/#zoeken, #divContent .kolom.links .blok.vol *, .article-list .list-item--odd .list-item__link {
    background-color: transparent;
}

/*No Border*/.grid-element .element-border, .article-list li ul > li:nth-child(2n), .comment, .article-list .list-item--odd .list-item__link {
    border: none;
}

/*Display None*/
footer .footer-row:nth-child(2), .site__footer {
    display: none;
}

.hierachical-timing .animated:not(:hover), .grid-element:not(:hover), .article-detail .article-media:not(:hover) {
    opacity: .7 !important; 
}

/*Header/Footer*/
    .nav-site {
        background: linear-gradient(to bottom, rgba(77, 75, 76, .7) 0px, rgba(23, 23, 23, .7) 100%);
    }

    .nav-site .nav-site-account {
        background: none;
        border: 0;
    }

    .site--footer .footer-row:first-child {
        background: rgba(38, 33, 37, .7);
        border-bottom: 0;
    }
/*Header/Footer*/

/*Block header*/
    .frontpage-filter, .block header, .rs-block--header {
        background: linear-gradient(to bottom, rgba(77, 75, 76, .7) 0px, rgba(23, 23, 23, .7) 100%);
    }

    .frontpage-filter li.active a {
        background: rgba(0, 0, 0, .7); 
    }
/*Block header*/

/*Article */
    .article-list, .rs-block, .article-detail .article-breadcrumb, .article-detail header, .article-detail .article-detail-content, .article-author-info, .article-pagination, .comments-list, .breadcrumb {
        background: rgba(255, 255, 255, .7);
    /*     background: none; */
        border: 0;
    }

    .article-list li.odd {
        background: none;
        border: 0;
    }

    /*Hover*/[class="list-item list-item--odd"]:hover, [class="list-item list-item--even"]:hover {
        background: rgba(249, 249, 249, .7)
    }

    .article-list a:hover {
        color: rgb(190, 3, 0) !important;
    }
/*Article */

/*Sidebar right*/
    #qp_main1903392, div.block:nth-child(6) > div:nth-child(2), div.block:nth-child(7) > div:nth-child(2) {
        background: transparent !important;
    }

    div.block:nth-child(5) > div:nth-child(2)  {
        background: linear-gradient(transparent 72px, rgba(255, 255, 255, .7) 72px, rgba(255, 255, 255, .7)) !important;
    }

    .rs-block {
        background: linear-gradient(transparent 54px, rgba(255, 255, 255, .7) 54px, rgba(255, 255, 255, .7)) !important;
    }

    #qp_main1903392 > div {
        background: rgba(190, 3, 0, .7) !important;
    }
    
    div.rs-snippet img:not(:hover) {
        opacity: .7;
    }
/*Sidebar right*/
    

    
    
/*Text alles White + shadow*/
test {
    color: white; 
    text-shadow: 0px 0px 3px black, 0px 0px 3px black, 0px 0px 3px black;
}
    
test {
    color: white !important;
}

/*No Shadow*/test {
    text-shadow: none !important;
}

test {
    text-shadow: none !important;
    color: black !important;
}

}


/*Artiekel lezen, conflict met frontpage*/
@-moz-document url-prefix("https://gamer.nl/articles/") {

/*reading*/
.page > article.columns-2 > .sections {
/*     width: 90%; */
}

/* .page > article.columns-2 > .sections {
    right: unset;
    left: unset;
    float: none;
    margin-left: auto !important;
    margin-right: auto;
} */

.article-detail .article-detail-content, .article-detail .article-breadcrumb, .article-detail header, .article-pagination, .comment, .page > article.columns-2 > aside .block--content {
    background: rgba(255, 255, 255, .80);
    border: 0;
}

.comments-list {
    background: none;
}

.block--list-small .block--list-item {
    border: 0;
}
/*reading*/

}
