css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dashboard.vkistudios.net") {

  body{
    font-family:"Times New Roman" !important;
    font-size: 10pt !important;
  }
  select, input {
    font-size: 8pt !important;
    font-family: "Arial", sans-serif !important;
  }
  select {

  }

  div#listControls {
    width: 60% !important;
    margin: 0 auto !important;
    border: none !important;
  }
  div#listControls_row3 {
    padding: 0 !important;
  }
  #listControls .filterSearch {
    width: auto !important;
  }
  div#listControls div.listControlsRow {
/* border: 1px solid blue !important; */
  }

  table#projectList {
    width: 0 !important;
    margin: 0.5em auto 0 auto !important;
    border: none !important;
    border-collapse: collapse !important;
  }
  table#projectList tr td {
    padding: 0 0.5em !important;
    border: none !important;
  }
  table.tablesorter tbody td {
    vertical-align: middle !important;
    white-space: nowrap !important;
  }
  table#projectList tr {
    border-bottom: 1px solid #ccc !important;
  }

  #projectList .harvest-missing td, #projectList .basecamp-missing td {
    background-color: #fdd !important;
  }


}
