 body, html, .cnn_contentarea {background: rgba(0,0,0,.55)url(/*[[URL]]*/)!important; 
  background-color: #111 !important;
	background-attachment: fixed !important;
	background-clip: border-box !important;
	background-origin: padding-box !important;
	background-position: center center !important;
	background-repeat: no-repeat !important;
	background-size: cover !important;
  font-family:Constantia!important}
			
	#menu, tr.backgr, #download, .hidehead, .hidebody, div#index tr.tum:hover, #index>table>tbody>tr:hover {background: rgba(/*[[menu]]*/ 0.35)!important; box-shadow: 0px 0px 5px rgba(0, 191, 255, .9 ) inset!important;border: 0px !important}
	.news_date{border: 0px !important}
  .menu_b div, div#index tr.tum,td,tr{background:transparent !important}
	
	
	.menu_b div, h1,h2,.news_title a:hover, #news_table, div#down, .d_small:hover, a:hover    {color: /*[[color1]]*/ !important;text-decoration: none !important}	
	.news_title a, div#index tr a, td, h2 a, .green, .red, .d_small, a  {color: /*[[color2]]*/ !important;text-decoration: none !important}	
	div#index tr a:hover, h2 a:hover  {color: /*[[color3]]*/!important;text-decoration: none !important}
	
  div[onmouseup],img[src*="s.rutor.org/t/"], div#MarketGid5190, div.sideblock2, iframe,noindex, #details>tbody>tr>td>a>img {display:none!important}

	div#ws div#sidebar div.sideblock {border-color: #3399ff !important; margin-top: 5px;margin-left: 8px;}
		
  #in, #sub{background:-moz-linear-gradient(top, #000, #222, #000) #000 no-repeat !important;	color: #3399ff !important;border-color: #3399ff !important; text-align: center !important}
	
	.backgr > td > img { height: 0px;width: 60px;}
  .logout > img {height: 27px;}

  /*[[logo]]*/
  
	.downgif > img {height: 12px; width: 14px;visibility: hidden;}
	a.downgif { background-image:	url("data:image/png;base64,") !important;background-repeat: no-repeat;}
	

	
	

