css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.webzen.com") {
  #wzmods-top-page #wzmods-logo-link:before { /*showVersion*/
    content: "Theme v1.0b:";
  }

  /* MOD CREDITS
   * This version has been modified by the following individuals:
   *
   * Mochatroid [May - June 2014] mochatroid.com
   *
   *
   *
   * If you make your own modifications, please add yourself to the list before distributing it.
   * Please and thank you :)
  */

  .post-signature img { 
   max-height: 200px !important;
  }

  #wrapper {
    background-attachment: scroll !important;
    background-clip: border-box !important;
    background-color: #1c1b18 !important;
    background-image: url("http://rappelz-forum.gpotato.com/images/gpotato/bg_articleCenter_top.jpg") !important;
    background-origin: padding-box !important;
  
    background-repeat: no-repeat !important;
    background-size: 100% auto !important;
  
  
    min-height: 389px !important;

  }

  .conversation-toolbar {
  background-color: #43413a !important;
  }

  #header {
    background-color: #272822 !important;
    background-image: url("http://rappelz-forum.gpotato.com/images/gpotato/bg_forum_navi_area01.gif") !important;
  }
  
  .main-title {
    color: #f4d168 !important;
  }
  
  #breadcrumbs {
    height: 30px !important;
  }
  
  #breadcrumbs .crumb-link { 
  background-color: #e0a05a !important;
  }
  #breadcrumbs a {
    color: #e0a05a !important;
    background: repeating-linear-gradient(
      -180deg,
      #4b2d0e,
      #4b2d0e 10px,
      #2e1e08 12px,
      #2e1e08 20px
    ) !important;
    padding-top: 5px !important;
    padding-bottom: 6px !important;
  }
  
  .ellipsis {
    overflow: visible !important;
  }
  .crumb-link {
    color: #e0a05a !important;
  }
  .crumb a {
    color: #e0a05a !important;
  }

  #thread-view-tab .b-post { 
  background: #181510 !important; /* main post bg colour */
  }
  #thread-view-tab .b-post:nth-of-type(2n) { 
  background: #1f1c16 !important; /* 2nd post bg colors */
  color: #ADADAD !important;
  }
  
  /* TIMESTAMP MOVE */
  
  .b-post__timestamp  {
    margin-left: 300px !important;
  }

    /* main post transparency */
  #thread-view-tab .conversation-list li:nth-child(2n) .b-post__body { 
  background: transparent !important; /*second post backgrounds*/
  }

  #thread-view-tab .conversation-list li:nth-child(1n+1) .b-post__body { 
  background: transparent !important; /*second post backgrounds*/
 
  }
  #thread-view-tab .conversation-list li:nth-child(2n+1) .b-post__body {
    background: none repeat scroll 0% 0% transparent !important;
  }
  
  /* /end main post background transparency */
  
  #thread-view-tab .b-post { 
  border: 1px solid #000 !important;
  }
  .bg2 .content {
  color: #FF4000 !important;
  }
  js-post__content-text OLD__post-content-text restore h-wordwrap {
  color: #FF4000 !important;
  }
  .widget-content, .post-content {
  color: #FF5400 !important;
  }
  OLD__post-content js-post__content h-clear {
  color: #000000 !important;
  }
  .b-comments {
  color: #000000 !important;
  }

  .b-avatar--m > img { 
  max-height: 100px !important;
  max-width: 100px !important;
  }

  .b-avatar--m {
  height: 100px !important;
  width: 100px !important;
  border-color: #606060 !important;
  }
  
  /* player pip icons */
  .b-meter__bar {
    display: none !important;
  }
  /* Join Date and Posts text bits */
  .b-userinfo__additional-info label {
    color: #909090;
  }
  /* activity radar */
  .b-icon {
    display: none !important;
  }
  .l-col__fixed-3, .l-row__fixed--left > .l-col__fixed-3 {
  float: right !important;
  }
  .l-row, .l-row__fixed--left {
  float: right !important;
  }
  .l-col__flex-3, .l-row__fixed--left > .l-col__flex-3 {
  float: right !important;
  margin-left: 0px !important;
  width: 870px !important;
  margin-right: 0px !important;
  }
  .l-row .l-row__fixed--left {
  float: right !important;
  }

  .b-userinfo {
  display: inline !important;
  float: right !important;
  }

  .b-post__arrow {
  display: none !important;
  }
  .b-userinfo { 
  padding: 15px 0px 10px !important;
  }

  .h-padding-horiz-xxl { 
  padding-left: 0px;
  padding-right: 0px;
  }
 

/* coments bg */
.b-comments {
  background-color: #aeaeae !important;
}
/* 1-step comments */
  li .b-comment{
    background-color: #ff0000 !important;
  }
  #thread-view-tab li .b-post{
    background-color: #a31f07 !important;
  }

  #thread-view-tab li .b-comment {
    background-color: #141414 !important; /* 1st comment bg colors */
    color: #ADADAD !important
  }
  
  #thread-view-tab li .b-post:nth-of-type(2n) { 
    background-color: #1a1a1a !important; /* 2nd comment bg colors */
    color: #ADADAD !important
  }

  #thread-view-tab .userinfo .author a { 
  color: #DADADA !important;
  }
  .b-userinfo { 
  color: #747474;
  }

  .post-signature {
    margin-top: 15px !important;
    padding-top: 10px !important;
    border-top: dotted thin #44201e !important;
  }
  /*bottom of posts - remove white line*/
  .b-comments__comments {
    border-bottom: none !important;
  }
  .b-divider {
    width: 100% !important;
    margin-top: 15px !important;    
    margin-bottom: 5px !important;
    border-top: dotted thin #44201e !important;
  }
  
  /*FORUM INDEX*/

  .topic-list-container .topic-list {
    background: none repeat scroll 0px 0px #181510 !important;
    color: #ADADAD !important;
  }
  .topic-list-container .topic-list .topic-title {   
    color: #ADADAD !important;
  }
  .channel-conversation-list-wrapper .topic-list .topic-item:nth-child(2n) {
    background: none repeat scroll 0% 0% #1f1c16 !important;
  }
  /*oldschool hover-y goodness*/
  .channel-conversation-list-wrapper .topic-list .topic-item:hover {
    color: #000;
    background: none repeat scroll 0% 0% #f0ebc1 !important;
  }
    /*A world with no borders! xD*/
    .topic-list-container .topic-list .topic-item td {
      border-top: 1px solid #000000 !important;
  }
}
